/* MobileStoreDayOfWeekSaleMapper.getMobileStoreDayOfWeekSaleList */ SELECT STORE_CD, STORE_NM, YOIL, YOIL2, SUM(SALE_DATE) AS SALE_DATE, SUM(BILL_CNT) AS BILL_CNT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT tsdt.STORE_CD, tms.STORE_NM, TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYY-MM-DD'),'DY') AS YOIL, TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYY-MM-DD'),'d') AS YOIL2, COUNT(tsdt.SALE_DATE) AS SALE_DATE, SUM(tsdt.BILL_CNT) AS BILL_CNT, SUM(tsdt.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt, TB_MS_STORE tms WHERE tms.STORE_CD = tsdt.STORE_CD AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN
#{item}
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdt.STORE_CD, tms.STORE_NM, tsdt.SALE_DATE )
GROUP BY ROLLUP((STORE_CD, STORE_NM), (YOIL, YOIL2)) ORDER BY STORE_CD, YOIL2
GROUP BY ROLLUP((YOIL, YOIL2), (STORE_CD, STORE_NM)) ORDER BY YOIL2, STORE_CD
