AND
tsdt.STORE_CD = #{item}
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tms.HQ_OFFICE_CD = tsdt.HQ_OFFICE_CD AND tms.STORE_CD = tsdt.STORE_CD ORDER BY tsdt.STORE_CD, tms.STORE_NM, tsdt.SALE_DATE
