mapper.select:
/* MobileStoreSaleMapper.getMobileStoreSaleList */ SELECT tsdt.STORE_CD, tms.STORE_NM, SUM(tsdt.REAL_SALE_CNT) AS BILL_CNT, SUM(tsdt.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt, TB_MS_STORE tms WHERE tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) ) AND tms.STORE_CD = tsdt.STORE_CD AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdt.STORE_CD, tms.STORE_NM ORDER BY tsdt.STORE_CD, tms.STORE_NM

/* MobileStoreSaleMapper.getMobileStoreSaleDtlList */
SELECT 	TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'),'YYYY-MM-DD') AS SALE_DATE,
        REAL_SALE_CNT AS BILL_CNT,
        REAL_SALE_AMT
FROM 	TB_SL_DAILY_TOTAL
WHERE 	HQ_OFFICE_CD = #{hqOfficeCd}
AND 	STORE_CD = #{storeCd}
AND 	SALE_DATE BETWEEN #{startDate} AND #{endDate}
ORDER BY SALE_DATE
