/* MobileTimeDaySaleMapper.getMobileTimeDaySaleDateTimeList */

SELECT
TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS SALE_DATE
${sQuery2}
FROM
(
    SELECT
    tsdt.SALE_DATE
    ${sQuery1}
    FROM TB_SL_DAILY_TIME tsdt
    WHERE 1=1
    AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}


        AND tsdt.STORE_CD IN

            #{item}



        AND (
                'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
            OR	tsdt.STORE_CD IN (SELECT STORE_CD  FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
            )

    AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
    AND tsdt.SALE_HOUR BETWEEN #{startTime} AND #{endTime}
    GROUP BY tsdt.SALE_DATE, tsdt.SALE_HOUR
    ORDER BY tsdt.SALE_DATE, tsdt.SALE_HOUR
)
GROUP BY SALE_DATE
ORDER BY SALE_DATE


SELECT
TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS SALE_DATE
${sQuery2}
FROM
(
    SELECT
    SALE_DATE
    ${sQuery1}
    FROM (SELECT    SALE_DATE,
                    REAL_SALE_AMT,
                    TIME_SLOT
          FROM  TB_SL_DAILY_TIME tsdt,
                (
                SELECT  MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) || LPAD(MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) + 1, 2, '0') AS TIME_SLOT,
                        MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) AS START_TIME,
                        MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) AS END_TIME


                    FROM    TB_HQ_NMCODE
                    WHERE 	HQ_OFFICE_CD = #{hqOfficeCd}



                    FROM 	TB_MS_STORE_NMCODE
                    WHERE 	STORE_CD = #{storeCd}

                AND		NMCODE_GRP_CD ='001'
                GROUP BY NMCODE_NM
                ) thn
            WHERE 1=1
            AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}


                AND tsdt.STORE_CD IN

                    #{item}



                AND (
                        'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
                    OR	tsdt.STORE_CD IN (SELECT STORE_CD  FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
                    )

            AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
            AND tsdt.SALE_HOUR BETWEEN thn.START_TIME AND thn.END_TIME

                AND TIME_SLOT = replace(#{timeSlot},'~','')

        )
    GROUP BY SALE_DATE
)
GROUP BY SALE_DATE
ORDER BY SALE_DATE

/* MobileTimeDaySaleMapper.getMobileTimeDaySaleTimeList */
SELECT A.SALE_TIME, A.AMT_CNT, A.REAL_SALE_AMT, DECODE(B.TOT_REAL_SALE_AMT, 0, 0, NVL(ROUND(A.REAL_SALE_AMT / B.TOT_REAL_SALE_AMT * 100, 2), 0)) AS AMT_RATE FROM ( SELECT tsdt.SALE_HOUR AS SALE_TIME, NVL(SUM(tsdt.REAL_SALE_CNT), 0) AS AMT_CNT, NVL(SUM(tsdt.REAL_SALE_AMT), 0) AS REAL_SALE_AMT FROM TB_SL_DAILY_TIME tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdt.SALE_HOUR BETWEEN #{startTime} AND #{endTime} GROUP BY tsdt.SALE_HOUR ) A, ( SELECT NVL(SUM(tsdt.REAL_SALE_AMT), 0) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_TIME tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} ) B ORDER BY A.SALE_TIME
SELECT A.SALE_TIME, A.AMT_CNT, A.REAL_SALE_AMT, DECODE(B.TOT_REAL_SALE_AMT, 0, 0, NVL(ROUND(A.REAL_SALE_AMT / B.TOT_REAL_SALE_AMT * 100, 2), 0)) AS AMT_RATE FROM ( SELECT TIME_SLOT AS SALE_TIME, NVL(SUM(tsdt.SALE_CNT) + SUM(tsdt.RTN_SALE_CNT), 0) AS AMT_CNT, NVL(SUM(tsdt.REAL_SALE_AMT), 0) AS REAL_SALE_AMT FROM TB_SL_DAILY_TIME tsdt, ( SELECT MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) || '~' || LPAD(MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) + 1, 2, '0') AS TIME_SLOT, MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) AS START_TIME, MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) AS END_TIME
FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
FROM TB_MS_STORE_NMCODE WHERE STORE_CD = #{storeCd}
AND NMCODE_GRP_CD ='001' GROUP BY NMCODE_NM ) thn WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdt.SALE_HOUR BETWEEN thn.START_TIME AND thn.END_TIME
AND TIME_SLOT = #{timeSlot}
GROUP BY TIME_SLOT ) A, ( SELECT NVL(SUM(tsdt.REAL_SALE_AMT), 0) AS TOT_REAL_SALE_AMT FROM TB_SL_DAILY_TIME tsdt WHERE 1=1 AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} ) B ORDER BY A.SALE_TIME

/* MobileTimeDaySaleMapper.getMobileTimeDaySaleTimeChartList */

SELECT
(tsdt.SALE_HOUR || '시') AS SALE_TIME,
NVL(SUM(tsdt.REAL_SALE_AMT), 0) AS REAL_SALE_AMT
FROM TB_SL_DAILY_TIME tsdt
WHERE 1=1
AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}


    AND tsdt.STORE_CD IN

        #{item}



    AND (
            'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
        OR	tsdt.STORE_CD IN (SELECT STORE_CD  FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
        )

AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdt.SALE_HOUR BETWEEN #{startTime} AND #{endTime}
GROUP BY tsdt.SALE_HOUR
ORDER BY tsdt.SALE_HOUR


SELECT
TIME_SLOT || '시' AS SALE_TIME,
NVL(SUM(tsdt.REAL_SALE_AMT), 0) AS REAL_SALE_AMT
FROM TB_SL_DAILY_TIME tsdt,
    (
    SELECT 	MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) || '~' || LPAD(MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) + 1, 2, '0') AS TIME_SLOT,
            MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) AS START_TIME,
            MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) AS END_TIME


        FROM    TB_HQ_NMCODE
        WHERE 	HQ_OFFICE_CD = #{hqOfficeCd}



        FROM 	TB_MS_STORE_NMCODE
        WHERE 	STORE_CD = #{storeCd}

    AND		NMCODE_GRP_CD ='001'
    GROUP BY NMCODE_NM
    ) thn
WHERE 1=1
AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}


    AND tsdt.STORE_CD IN

        #{item}



    AND (
            'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
        OR	tsdt.STORE_CD IN (SELECT STORE_CD  FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
        )

AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdt.SALE_HOUR BETWEEN thn.START_TIME AND thn.END_TIME

    AND SALE_TIME = #{timeSlot}

GROUP BY TIME_SLOT
ORDER BY TIME_SLOT
