mapper.select:
/* MobileTimeDaySaleMapper.getMobileTimeDaySaleTimeList */ SELECT A.SALE_TIME, A.AMT_CNT, A.REAL_SALE_AMT, NVL(ROUND(A.REAL_SALE_AMT / DECODE(B.TOT_REAL_SALE_AMT, 0, null, B.TOT_REAL_SALE_AMT) * 100, 2), 0) AS AMT_RATE FROM ( SELECT SALE_TIME, COUNT(DISTINCT BILL_NO) AS AMT_CNT, NVL(SUM(REAL_SALE_AMT), 0) AS REAL_SALE_AMT FROM ( SELECT tssh.BILL_NO, SUBSTR(tssh.REG_DT, 9, 2) AS SALE_TIME, tssh.REAL_SALE_AMT FROM TB_SL_SALE_HDR tssh WHERE 1=1 AND tssh.HQ_OFFICE_CD = #{hqOfficeCd} AND tssh.STORE_CD IN
#{item}
AND tssh.SALE_DATE BETWEEN #{startDate} AND #{endDate} ) WHERE 1=1 AND SALE_TIME BETWEEN #{startTime} AND #{endTime} GROUP BY SALE_TIME ) A, ( SELECT NVL(SUM(tssh.REAL_SALE_AMT), 0) AS TOT_REAL_SALE_AMT FROM TB_SL_SALE_HDR tssh WHERE 1=1 AND tssh.HQ_OFFICE_CD = #{hqOfficeCd} AND tssh.STORE_CD IN
#{item}
AND tssh.SALE_DATE BETWEEN #{startDate} AND #{endDate} ) B ORDER BY A.SALE_TIME
