mapper.update:
/* USE : VerAddrMapper.verModify */
UPDATE TB_CM_POS_ADDR_VERSN SET VER_SER_NM = #{verSerNm},
FILE_DIR = #{fileDir}, FILE_NM = #{fileNm}, FILE_EXT = #{fileExt}, FILE_SIZE = #{fileSize}, FILE_MIME_TYPE = #{fileMimeType}, FILE_ORG_NM = #{fileOrgNm},
FILE_DESC = #{fileDesc}, PROG_FG = #{progFg}, PGM_YN = #{pgmYn}, DB_YN = #{dbYn}, IMG_YN = #{imgYn}, USE_YN = #{useYn}, MOD_DT = #{modDt}, MOD_ID = #{modId} WHERE VER_SER_NO = #{verSerNo}

/* USE : VerAddrMapper.deleteVersion */
UPDATE TB_CM_POS_ADDR_VERSN
   SET DEL_YN = 'Y',
       USE_YN = 'N'
 WHERE VER_SER_NO = #{verSerNo}

mapper.select:
/* USE : VerAddrMapper.chkVerSerNo */
SELECT COUNT(1) AS CNT
  FROM TB_CM_POS_ADDR_VERSN
 WHERE VER_SER_NO = #{verSerNo}

mapper.delete:
/* USE : VerAddrMapper.removeStore */

DELETE TB_CM_POS_ADDR_VERSN_STORE
 WHERE VER_SER_NO = #{verSerNo}
   AND STORE_CD = #{storeCd}

mapper.select:
SELECT NVL(COUNT(tms.STORE_CD), 0) FROM TB_MS_STORE tms , ( SELECT STORE_CD, COUNT(POS_NO) AS POS_CNT FROM TB_MS_POS WHERE USE_YN ='Y' GROUP BY STORE_CD ) tmp WHERE 1=1 AND tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tms.STORE_CD = #{storeCd} AND tmp.STORE_CD = tms.STORE_CD AND tms.STORE_CD NOT IN ( SELECT STORE_CD FROM TB_HQ_POS_ADVER_STORE WHERE ADVER_FILE_NO = #{verSerNo} )
-- 선택한 매장브랜드가 없을 때('전체' 일때), 사용자 관리브랜드에 속한 정보만 조회 AND tms.HQ_BRAND_CD IN
#{item}

mapper.insert:
/* USE : VerManageMapper.storeExcelUpload */
MERGE INTO TB_HQ_POS_ADVER_STORE A
USING DUAL
ON (
            A.HQ_OFFICE_CD  = #{hqOfficeCd}
        AND A.ADVER_FILE_NO = #{verSerNo}
        AND A.STORE_CD      = #{storeCd}
    )
WHEN MATCHED THEN
UPDATE
SET
    MOD_DT = #{modDt}
,   MOD_ID = #{modId}
WHEN NOT MATCHED THEN
INSERT
(
     HQ_OFFICE_CD
,    ADVER_FILE_NO
,    STORE_CD
,    REG_DT
,    REG_ID
,    MOD_DT
,    MOD_ID
)
VALUES
(
     #{hqOfficeCd}
,    #{verSerNo}
,    #{storeCd}
,    #{regDt}
,    #{regId}
,    #{modDt}
,    #{modId}
)
