mapper.update:
/* USE : VerManageMapper.verModify */
UPDATE TB_CM_POS_VERSN SET VER_SER_NM = #{verSerNm},
FILE_DIR = #{fileDir}, FILE_NM = #{fileNm}, FILE_EXT = #{fileExt}, FILE_SIZE = #{fileSize}, FILE_MIME_TYPE = #{fileMimeType}, FILE_ORG_NM = #{fileOrgNm},
FILE_DESC = #{fileDesc}, PROG_FG = #{progFg}, PGM_YN = #{pgmYn}, DB_YN = #{dbYn}, IMG_YN = #{imgYn}, USE_YN = #{useYn}, MOD_DT = #{modDt}, MOD_ID = #{modId}, ORGN_CDS = #{orgnCds}, PROG_DETAIL_FG = #{progDetailFg}, SYSTEM_TYPE_FG = #{systemTypeFg} WHERE VER_SER_NO = #{verSerNo}

/* USE : VerManageMapper.deleteVersion */
UPDATE TB_CM_POS_VERSN
   SET DEL_YN = 'Y',
       USE_YN = 'N'
 WHERE VER_SER_NO = #{verSerNo}

mapper.select:
/* USE : VerManageMapper.registStore */
{
    CALL PKG_CM_VERSN_STORE.SP_CM_VERSN_STORE_I
    (
        #{verSerNo},
        #{storeCd},
        #{regDt},
        #{regId},
        #{modDt},
        #{modId},
        #{result, mode=OUT, jdbcType=VARCHAR}
    )
}

/* USE : VerManageMapper.chkVerSerNo */
SELECT COUNT(1) AS CNT
  FROM TB_CM_POS_VERSN
 WHERE VER_SER_NO = #{verSerNo}

mapper.delete:
/* USE : VerManageMapper.removeStore */

DELETE TB_CM_POS_VERSN_STORE
 WHERE VER_SER_NO = #{verSerNo}
   AND STORE_CD = #{storeCd}
