mapper.update:
/* USE : VerManageMapper.verModify */
UPDATE TB_CM_POS_VERSN SET VER_SER_NM = #{verSerNm},
FILE_DIR = #{fileDir}, FILE_NM = #{fileNm}, FILE_EXT = #{fileExt}, FILE_SIZE = #{fileSize}, FILE_MIME_TYPE = #{fileMimeType}, FILE_ORG_NM = #{fileOrgNm},
FILE_DESC = #{fileDesc}, PROG_FG = #{progFg}, PGM_YN = #{pgmYn}, DB_YN = #{dbYn}, IMG_YN = #{imgYn}, USE_YN = #{useYn}, MOD_DT = #{modDt}, MOD_ID = #{modId} WHERE VER_SER_NO = #{verSerNo}

/* USE : VerManageMapper.deleteVersion */
UPDATE TB_CM_POS_VERSN
   SET DEL_YN = 'Y',
       USE_YN = 'N'
 WHERE VER_SER_NO = #{verSerNo}

mapper.select:
/* USE : VerManageMapper.srchStoreList */ SELECT tms.STORE_CD, tms.SYS_STAT_FG, tms.VAN_CD, tms.CLS_FG, tms.STORE_NM, tho.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tcp.LAST_VER_SER_NO, tmp.POS_CNT FROM TB_MS_STORE tms, TB_HQ_OFFICE tho, ( SELECT STORE_CD, MAX(VER_SER_NO) AS LAST_VER_SER_NO FROM TB_CM_POS_VERSN_STORE GROUP BY STORE_CD ) tcp, ( SELECT STORE_CD, COUNT(POS_NO) AS POS_CNT FROM TB_MS_POS WHERE USE_YN ='Y' GROUP by STORE_CD ) tmp, ( SELECT STORE_CD FROM TB_MS_STORE_ENVST WHERE ENVST_CD = '1014' AND ENVST_VAL = #{progFg} )tmse WHERE tho.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tmp.STORE_CD = tms.STORE_CD
AND tms.STORE_CD IN (SELECT STORE_CD FROM TB_CM_POS_VERSN_STORE WHERE VER_SER_NO = #{verSerNo} )
AND tms.STORE_CD NOT IN (SELECT STORE_CD FROM TB_CM_POS_VERSN_STORE WHERE VER_SER_NO = #{verSerNo} )
AND tcp.STORE_CD (+)= tms.STORE_CD AND tmse.STORE_CD = tms.STORE_CD
AND tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND tho.HQ_OFFICE_NM LIKE '%'|| #{hqOfficeNm} ||'%'
AND tms.AGENCY_CD = #{agencyCd}
AND tms.STORE_CD LIKE '%'|| #{storeCd}||'%'
AND tms.STORE_CD IN
#{item}
AND tms.STORE_NM LIKE '%'|| #{storeNm} ||'%'
AND tms.SYS_STAT_FG = #{sysStatFg}
ORDER BY HQ_OFFICE_CD, STORE_CD

/* USE : VerManageMapper.registStore */
{
    CALL PKG_CM_VERSN_STORE.SP_CM_VERSN_STORE_I
    (
        #{verSerNo},
        #{storeCd},
        #{regDt},
        #{regId},
        #{modDt},
        #{modId},
        #{result, mode=OUT, jdbcType=VARCHAR}
    )
}

/* USE : VerManageMapper.chkVerSerNo */
SELECT COUNT(1) AS CNT
  FROM TB_CM_POS_VERSN
 WHERE VER_SER_NO = #{verSerNo}

mapper.delete:
/* USE : VerManageMapper.removeStore */

DELETE TB_CM_POS_VERSN_STORE
 WHERE VER_SER_NO = #{verSerNo}
   AND STORE_CD = #{storeCd}
