mapper.select:
/* VerRecvMapper.selectVerList */
SELECT tcpv.VER_SER_NO, tcpv.VER_SER_NM, tcpv.USE_YN, TO_CHAR(TO_DATE(tcpv.REG_DT, 'YYYYMMDDHH24MISS'), 'YYYY.MM.DD HH24:MI:SS') REG_DT, tcpv.REG_ID, TO_CHAR(TO_DATE(tcpv.MOD_DT, 'YYYYMMDDHH24MISS'), 'YYYY.MM.DD HH24:MI:SS') MOD_DT, tcpv.MOD_ID, NVL(tcpvs.REG_CNT, 0) REG_CNT, NVL(tcpvs.RECV_CNT, 0) RECV_CNT
FROM TB_CM_POS_VERSN tcpv, ( SELECT VER_SER_NO, SUM(DECODE(VER_RECV_FG, '1', 1, '2', 1, 0)) REG_CNT, SUM(DECODE(VER_RECV_FG , '2' , 1, 0)) RECV_CNT FROM TB_CM_POS_VERSN_STORE GROUP BY VER_SER_NO ) tcpvs WHERE tcpvs.VER_SER_NO (+)= tcpv.VER_SER_NO
AND tcpv.VER_SER_NO = #{verSerNo}
AND tcpv.VER_SER_NM = #{verSerNm}
ORDER BY tcpv.VER_SER_NO DESC

/* VerRecvMapper.selectStoreList */
SELECT tcpvs.VER_SER_NO, tcpvs.STORE_CD, tms.STORE_NM, tcpvs.VER_RECV_FG, TO_CHAR(TO_DATE(tcpvs.VER_RECV_DT, 'YYYYMMDDHH24MISS'), 'YYYY.MM.DD HH24:MI:SS') VER_RECV_DT, tcpvs.POS_IP, TO_CHAR(TO_DATE(tcpvs.REG_DT, 'YYYYMMDDHH24MISS'), 'YYYY.MM.DD HH24:MI:SS') REG_DT, tcpvs.REG_ID, TO_CHAR(TO_DATE(tcpvs.MOD_DT, 'YYYYMMDDHH24MISS'), 'YYYY.MM.DD HH24:MI:SS') MOD_DT, tcpvs.MOD_ID, tcpvs.POS_NO
FROM TB_CM_POS_VERSN_STORE tcpvs, TB_MS_STORE tms WHERE tcpvs.VER_SER_NO = #{verSerNo} AND tms.STORE_CD = tcpvs.STORE_CD ORDER BY tcpvs.STORE_CD

/* VerRecvMapper.selectStoreRecvList */
SELECT tho.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, tmp.POS_CNT FROM TB_HQ_OFFICE tho, TB_MS_STORE tms, ( SELECT tmp.STORE_CD, count(tmp.POS_NO) POS_CNT FROM TB_MS_POS tmp WHERE tmp.USE_YN = 'Y' GROUP BY tmp.STORE_CD ) tmp WHERE tms.HQ_OFFICE_CD = tho.HQ_OFFICE_CD AND tmp.STORE_CD = tms.STORE_CD
AND tho.HQ_OFFICE_CD = #{hqOfficeCd}
AND tho.HQ_OFFICE_NM = #{hqOfficeNm}
AND tms.STORE_CD = #{storeCd}
AND tms.STORE_NM = #{storeNm}
ORDER BY tho.HQ_OFFICE_CD, tms.STORE_CD

/* VerRecvMapper.selectVerStoreList */
SELECT tcpv.VER_SER_NO, tcpv.VER_SER_NM, tcpv.PROG_FG, tcpvs.STORE_CNT
FROM TB_CM_POS_VERSN tcpv, ( SELECT VER_SER_NO, COUNT(STORE_CD) AS STORE_CNT FROM TB_CM_POS_VERSN_STORE GROUP BY VER_SER_NO ) tcpvs WHERE tcpvs.VER_SER_NO = tcpv.VER_SER_NO
AND tcpv.VER_SER_NO = #{verSerNo}
AND tcpv.VER_SER_NM = #{verSerNm}
ORDER BY tcpv.VER_SER_NO DESC

/* VerRecvMapper.selectVerStoreDtlList */
SELECT tcpvs.VER_SER_NO, tcpvs.STORE_CD, tms.STORE_NM, tcpvs.VER_RECV_FG, TO_CHAR(TO_DATE(tcpvs.REG_DT, 'YYYYMMDDHH24MISS'), 'YYYY.MM.DD HH24:MI:SS') REG_DT, TO_CHAR(TO_DATE(tcpvs.VER_RECV_DT, 'YYYYMMDDHH24MISS'), 'YYYY.MM.DD HH24:MI:SS') VER_RECV_DT
FROM TB_CM_POS_VERSN_STORE tcpvs, TB_MS_STORE tms WHERE tcpvs.VER_SER_NO = #{verSerNo} AND tms.STORE_CD = tcpvs.STORE_CD ORDER BY tcpvs.STORE_CD ASC, tcpvs.REG_DT DESC, tcpvs.VER_RECV_DT DESC
