mapper.select:
/* VerRecvMapper.selectVerList */
SELECT tcpv.VER_SER_NO, tcpv.VER_SER_NM, tcpv.USE_YN, TO_CHAR(TO_DATE(tcpv.REG_DT, 'YYYYMMDDHH24MISS'), 'YYYY.MM.DD HH24:MI:SS') AS REG_DT, tcpv.REG_ID, TO_CHAR(TO_DATE(tcpv.MOD_DT, 'YYYYMMDDHH24MISS'), 'YYYY.MM.DD HH24:MI:SS') AS MOD_DT, tcpv.MOD_ID, NVL(tcpvs.REG_CNT, 0) AS REG_CNT, NVL(tcpvs.RECV_CNT, 0) AS RECV_CNT, tcpv.FILE_DESC
FROM TB_CM_POS_VERSN tcpv, ( SELECT VER_SER_NO, SUM(DECODE(VER_RECV_FG, '1', 1, '2', 1, 0)) REG_CNT, SUM(DECODE(VER_RECV_FG , '2' , 1, 0)) RECV_CNT FROM TB_CM_POS_VERSN_STORE GROUP BY VER_SER_NO ) tcpvs WHERE tcpvs.VER_SER_NO (+)= tcpv.VER_SER_NO
AND tcpv.VER_SER_NO = #{verSerNo}
AND tcpv.VER_SER_NM = #{verSerNm}
ORDER BY tcpv.REG_DT DESC

/* VerRecvMapper.selectStoreRecvList */
SELECT tho.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, NVL(tmp.POS_CNT, 0) AS POS_CNT FROM TB_HQ_OFFICE tho, TB_MS_STORE tms, ( SELECT tmp.STORE_CD, count(tmp.POS_NO) POS_CNT FROM TB_MS_POS tmp WHERE tmp.USE_YN = 'Y' GROUP BY tmp.STORE_CD ) tmp WHERE tms.HQ_OFFICE_CD = tho.HQ_OFFICE_CD AND tmp.STORE_CD = tms.STORE_CD
AND tho.HQ_OFFICE_CD = #{hqOfficeCd}
AND tho.HQ_OFFICE_NM LIKE '%'|| #{hqOfficeNm} || '%'
AND tms.STORE_CD = #{storeCd}
AND tms.STORE_NM LIKE '%'|| #{storeNm} ||'%'
ORDER BY tho.HQ_OFFICE_CD, tms.STORE_CD

/* VerRecvMapper.selectVerStoreList */
SELECT tcpv.VER_SER_NO, tcpv.VER_SER_NM, tcpv.PROG_FG, NVL(tcpvs.STORE_CNT, 0) AS STORE_CNT, tcpv.FILE_DESC
FROM TB_CM_POS_VERSN tcpv, ( SELECT VER_SER_NO, COUNT(STORE_CD) AS STORE_CNT FROM TB_CM_POS_VERSN_STORE GROUP BY VER_SER_NO ) tcpvs WHERE tcpvs.VER_SER_NO (+)= tcpv.VER_SER_NO
AND tcpv.VER_SER_NO = #{verSerNo}
AND tcpv.VER_SER_NM = #{verSerNm}
ORDER BY tcpv.REG_DT DESC, tcpv.VER_SER_NO DESC
