mapper.select:
/* InstallManageMapper.getInstallRequestList */
SELECT tci.STORE_CD, tms.STORE_NM , tci.POS_NO, tci.SEQ_NO, tci.INST_FG, tci.AGENCY_CD, tca.AGENCY_NM , TO_CHAR(TO_DATE(tci.INST_REQ_DT ,'YYYYMMDDhh24miss'), 'YYYY/MM/DD hh24:mi:ss') AS INST_REQ_DT, tci.INST_REQ_ID, TO_CHAR(TO_DATE(tci.INST_INS_DT ,'YYYYMMDDhh24miss'), 'YYYY/MM/DD hh24:mi:ss') AS INST_INS_DT, tci.INST_INS_ID, tci.INST_REASON, tci.REMARK
FROM TB_CM_INSTLL tci, TB_MS_STORE tms, TB_CM_AGENCY tca WHERE tms.STORE_CD = tci.STORE_CD AND tca.AGENCY_CD = tci.AGENCY_CD
AND tci.AGENCY_CD LIKE '%'|| #{agencyCd}||'%'
AND tca.AGENCY_NM LIKE '%'|| #{agencyNm} ||'%'
AND tci.AGENCY_CD = #{agencyCd}
AND tci.STORE_CD LIKE '%' ||#{storeCd}|| '%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tci.INST_FG = #{instFg}
ORDER BY INST_REQ_DT DESC

/* InstallManageMapper.getPosList */
SELECT tms.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tms.STORE_NM, tmp.STORE_CD, tmp.POS_NO, tms.SYS_STAT_FG, tci.SEQ_NO, tci.INST_FG, tms.AGENCY_CD, tca.AGENCY_NM, tci.INST_REASON, tci.REMARK
FROM TB_MS_POS tmp, TB_MS_STORE tms, TB_HQ_OFFICE tho, ( SELECT STORE_CD, POS_NO, MAX(SEQ_NO) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS SEQ_NO, MAX(INST_FG) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS INST_FG, MAX(AGENCY_CD) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS AGENCY_CD, MAX(INST_REASON) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS INST_REASON, MAX(REMARK) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS REMARK FROM TB_CM_INSTLL GROUP BY STORE_CD , POS_NO ) tci, TB_CM_AGENCY tca WHERE tms.STORE_CD = tmp.STORE_CD AND tho.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tci.STORE_CD (+)= tmp.STORE_CD AND tci.POS_NO (+)= tmp.POS_NO AND tca.AGENCY_CD (+)= tci.AGENCY_CD AND (tci.INST_FG != 0 OR tci.INST_FG IS NULL)
AND tms.HQ_OFFICE_CD LIKE '%' ||#{hqOfficeCd}|| '%'
AND tho.HQ_OFFICE_NM LIKE '%' ||#{hqOfficeNm}|| '%'
AND tms.STORE_CD LIKE '%' ||#{storeCd}|| '%'
AND tms.STORE_NM LIKE '%' ||#{storeNm}|| '%'
AND tms.AGENCY_CD = #{agencyCd}
AND tci.INST_FG = #{instFg}
ORDER BY tms.HQ_OFFICE_CD, tmp.STORE_CD, tmp.POS_NO,tci.SEQ_NO DESC

mapper.update:
/* InstallManageMapper.initPosHwKey */
UPDATE TB_MS_POS
   SET HW_AUTH_KEY = null,
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE STORE_CD = #{storeCd}
   AND POS_NO = #{posNo}
