mapper.select:
/* USE : InstlAgencyMapper.getInstlAgency */ SELECT tca.AGENCY_CD , tca.AGENCY_NM , tca.P_AGENCY_CD , tca.BIZ_NO , ('[' || tca.AGENCY_GRP || '] ' || (SELECT AGENCY_NM FROM TB_CM_AGENCY WHERE AGENCY_CD = tca.AGENCY_GRP)) AS AGENCY_GRP FROM ( SELECT tca.AGENCY_CD , tca.AGENCY_NM , tca.P_AGENCY_CD , tca.BIZ_NO , (CASE WHEN tca.P_AGENCY_CD = '00000' THEN tca.AGENCY_CD ELSE tca.P_AGENCY_CD END) AS AGENCY_GRP FROM TB_CM_AGENCY tca WHERE 1=1 AND tca.AGENCY_CD != '00000' ) tca WHERE 1=1
AND tca.P_AGENCY_CD = '00000'
AND tca.P_AGENCY_CD = #{agencyCd}
AND tca.AGENCY_CD LIKE '%'||#{srchAgencyCd}||'%'
AND tca.AGENCY_NM LIKE '%'||#{srchAgencyNm}||'%'
AND tca.BIZ_TYPE_CD = #{bizTypeCd}
AND tca.BIZ_NO LIKE '%'||#{bizNo}||'%'
ORDER BY tca.AGENCY_GRP, tca.P_AGENCY_CD, tca.AGENCY_CD

mapper.insert:
/* USE : InstlAgencyMapper.insertAgency */
INSERT INTO TB_CM_AGENCY
(
      AGENCY_CD
    , AGENCY_NM
    , OWNER_NM
    , P_AGENCY_CD
    , BIZ_NO
    , BIZ_TYPE_CD
    , BIZ_ITEM_CD
    , BIZ_STORE_NM
    , TEL_NO
    , FAX_NO
    , EMAIL_ADDR
    , HMPG_ADDR
    , POST_NO
    , ADDR
    , ADDR_DTL
    , AREA_CD
    , REMARK
    , REG_DT
    , REG_ID
    , MOD_DT
    , MOD_ID
)
VALUES
(
     (SELECT LPAD(NVL(MAX(AGENCY_CD),0)+1, 5, 0) FROM TB_CM_AGENCY)
    ,#{agencyNm}
    ,#{ownerNm}
    ,#{pAgencyCd}
    ,#{bizNo}
    ,#{bizTypeCd}
    ,#{bizItemCd}
    ,#{bizStoreNm}
    ,#{telNo}
    ,#{faxNo}
    ,#{emailAddr}
    ,#{hmpgAddr}
    ,#{postNo}
    ,#{addr}
    ,#{addrDtl}
    ,#{areaCd}
    ,#{remark}
    ,#{regDt}
    ,#{regId}
    ,#{modDt}
    ,#{modId}
)

mapper.update:
/* USE : InstlAgencyMapper.updateAgency */
UPDATE TB_CM_AGENCY
   SET AGENCY_NM = #{agencyNm}
      , OWNER_NM = #{ownerNm}
      , P_AGENCY_CD = #{pAgencyCd}
      , BIZ_NO = #{bizNo}

      , BIZ_STORE_NM  = #{bizStoreNm}
      , TEL_NO  = #{telNo}
      , FAX_NO  = #{faxNo}
      , EMAIL_ADDR  = #{emailAddr}
      , HMPG_ADDR  = #{hmpgAddr}
      , POST_NO  = #{postNo}
      , ADDR  = #{addr}
      , ADDR_DTL  = #{addrDtl}

      , REMARK = #{remark}
      , MOD_DT = #{modDt}
      , MOD_ID = #{modId}
 WHERE AGENCY_CD = #{agencyCd}

mapper.insert:
/* USE : InstlAgencyMapper.insertEmp */
INSERT INTO TB_CM_EMPLOYEE
(
  AGENCY_CD
, EMP_NO
, EMP_NM
, EMP_PWD
, WEB_USE_YN
, USER_ID
, MP_NO
, SERVICE_FG
, SMS_RECV_YN
, ADMIN_FG
, MAP_EMP_NO
, USE_YN
, REMARK
, REG_DT
, REG_ID
, MOD_DT
, MOD_ID
)
VALUES
(
 #{agencyCd}
,(SELECT LPAD(NVL(MAX(EMP_NO),0)+1, 4, 0) FROM TB_CM_EMPLOYEE)
,#{empNm}
,#{userPwd}
,#{webUseYn}
,#{userId}
,#{mpNo}
,#{serviceFg}
,#{smsRecvYn}
,#{adminFg}
,#{mapEmpNo}
,#{useYn}
,#{remark}
,#{regDt}
,#{regId}
,#{modDt}
,#{modId}
)

mapper.update:
/* USE : InstlAgencyMapper.updateEmployee */
UPDATE TB_CM_EMPLOYEE
    SET EMP_NM = #{empNm}

        , WEB_USE_YN = #{webUseYn}

        , MP_NO = #{mpNo}
        , SERVICE_FG = #{serviceFg}
        , SMS_RECV_YN = #{smsRecvYn}
        , ADMIN_FG = #{adminFg}
        , MAP_EMP_NO = #{mapEmpNo}
        , USE_YN = #{useYn}
        , REMARK = #{remark}
        , MOD_DT = #{modDt}
        , MOD_ID = #{modId}
WHERE AGENCY_CD = #{agencyCd}
  AND EMP_NO = #{empNo}
