mapper.select:
/* InstlManageMapper.getAgencyList */
SELECT tca.AGENCY_CD, tca.AGENCY_NM,
(CASE WHEN tca.P_AGENCY_CD = '00000' THEN '총판' WHEN tca.P_AGENCY_CD != '00000' THEN '대리점' ELSE '' END) AS AGENCY_FG,
(CASE WHEN tca.AGENCY_CD = #{orgnCd} THEN '자사' WHEN tca.P_AGENCY_CD = #{orgnCd} THEN '대리점' ELSE '' END) AS AGENCY_FG,
tca.OWNER_NM, 0 AS BUY_CNT, 0 AS SALE_CNT, NVL(tci.INST_CNT,0) AS INST_CNT, 0 AS REST_CNT, NVL(tms.TOT_STORE_CNT, 0) AS TOT_STORE_CNT, NVL(tsms.SALE_STORE_CNT, 0) AS SALE_STORE_CNT, NVL(tms.SYS_STAT_FG_2, 0) AS CLOSE_STORE_CNT, tca.USE_YN, TO_CHAR(TO_DATE(tca.REG_DT, 'YYYYMMDDHH24MISS'), 'yyyy-mm-dd') AS REG_DT, tca.REG_ID
FROM TB_CM_AGENCY tca, ( SELECT AGENCY_CD, COUNT(STORE_CD) AS INST_CNT FROM TB_CM_INSTLL WHERE 1=1 AND SEQ_NO = '1' AND INST_FG = '1' GROUP BY AGENCY_CD ) tci, ( SELECT tms.AGENCY_CD, COUNT(tms.AGENCY_CD) AS TOT_STORE_CNT, SUM(CASE WHEN tms.SYS_STAT_FG = 2 THEN 1 ELSE 0 END) AS SYS_STAT_FG_2 FROM TB_MS_STORE tms GROUP BY tms.AGENCY_CD ) tms, ( SELECT A.AGENCY_CD, COUNT(A.STORE_CD) AS SALE_STORE_CNT FROM ( SELECT tms.AGENCY_CD, tms.STORE_CD FROM TB_MS_STORE tms, TB_CM_AGENCY tca WHERE 1=1 AND tca.AGENCY_CD = tms.AGENCY_CD ORDER BY tms.AGENCY_CD, tms.STORE_CD ) A, ( SELECT STORE_CD FROM TB_SL_SALE_HDR GROUP BY STORE_CD ORDER BY STORE_CD ) B WHERE A.STORE_CD = B.STORE_CD GROUP BY A.AGENCY_CD ) tsms WHERE 1=1 AND tca.AGENCY_CD != '00000' AND tca.REG_DT BETWEEN #{startDate} AND #{endDate}
AND (tca.AGENCY_CD = #{orgnCd} OR tca.P_AGENCY_CD = #{orgnCd})
AND tca.AGENCY_CD LIKE '%'|| #{srchAgencyCd} ||'%'
AND tca.AGENCY_NM LIKE '%'|| #{srchAgencyNm} ||'%'
AND tca.AGENCY_CD = #{orgnCd}
AND tca.P_AGENCY_CD = #{orgnCd}
AND tca.USE_YN = #{useYn}
AND tci.AGENCY_CD (+)= tca.AGENCY_CD AND tms.AGENCY_CD (+)= tca.AGENCY_CD AND tsms.AGENCY_CD (+)= tca.AGENCY_CD ORDER BY tca.AGENCY_CD

/* InstlManageMapper.getInstlList */ SELECT tca.AGENCY_CD, tca.AGENCY_NM, NVL(tms.TOT_STORE_CNT, 0) AS TOT_STORE_CNT, NVL(tms.SYS_STAT_FG_2, 0) AS CLOSE_STORE_CNT, NVL(tmps.POS_CNT, 0) AS POS_CNT, NVL(tci.INST_0, 0) AS REST_CNT FROM TB_CM_AGENCY tca, ( SELECT tms.AGENCY_CD, COUNT(tms.AGENCY_CD) AS TOT_STORE_CNT, SUM(CASE WHEN tms.SYS_STAT_FG = 2 THEN 1 ELSE 0 END) AS SYS_STAT_FG_2 FROM TB_MS_STORE tms GROUP BY tms.AGENCY_CD ) tms, ( SELECT tms.AGENCY_CD, COUNT(tmp.POS_NO) AS POS_CNT FROM TB_MS_POS tmp, TB_MS_STORE tms WHERE 1=1 AND tmp.STORE_CD (+)= tms.STORE_CD GROUP BY tms.AGENCY_CD ) tmps, ( SELECT AGENCY_CD, SUM(CASE WHEN INST_FG = 0 THEN 1 ELSE 0 END) AS INST_0 FROM TB_CM_INSTLL GROUP BY AGENCY_CD ) tci WHERE 1=1 AND tca.AGENCY_CD != '00000'
AND tca.REG_DT BETWEEN #{startDate} AND #{endDate}
AND (tca.AGENCY_CD = #{agencyCd} OR tca.P_AGENCY_CD = #{agencyCd})
AND tca.AGENCY_CD LIKE '%'|| #{srchAgencyCd} ||'%'
AND tca.AGENCY_NM LIKE '%'|| #{srchAgencyNm} ||'%'
AND tms.AGENCY_CD (+)= tca.AGENCY_CD AND tmps.AGENCY_CD (+)= tca.AGENCY_CD AND tci.AGENCY_CD (+)= tca.AGENCY_CD ORDER BY tca.AGENCY_CD

/* InstlManageMapper.getInstlDetailList */
SELECT tci.STORE_CD, tms.STORE_NM, tci.POS_NO, tca.AGENCY_NM AS AGENCY_NM, (SELECT AGENCY_NM FROM TB_CM_AGENCY WHERE AGENCY_CD = tci.INSTL_AGENCY_CD) AS INSTL_AGENCY_NM, tci.INST_FG, tci.INST_INS_ID, tci.MIN_INST_INS_DT, tci.MAX_INST_INS_DT, tci.INST_CNT, tms.SYS_STAT_FG
FROM TB_MS_STORE tms, TB_CM_AGENCY tca, ( SELECT STORE_CD, POS_NO, AGENCY_CD AS INSTL_AGENCY_CD, MAX(INST_FG) KEEP(DENSE_RANK FIRST ORDER BY INST_INS_DT DESC) AS INST_FG, INST_INS_ID, MIN(TO_CHAR(TO_DATE(INST_INS_DT, 'YYYYMMDDHH24MISS'), 'yyyy-mm-dd')) AS MIN_INST_INS_DT, MAX(TO_CHAR(TO_DATE(INST_INS_DT, 'YYYYMMDDHH24MISS'), 'yyyy-mm-dd')) AS MAX_INST_INS_DT, COUNT(STORE_CD) AS INST_CNT FROM TB_CM_INSTLL WHERE 1=1 GROUP BY STORE_CD, POS_NO, AGENCY_CD, INST_INS_ID ORDER BY STORE_CD, POS_NO ) tci WHERE 1=1 AND tca.AGENCY_CD = #{agencyCd}
AND tca.REG_DT BETWEEN #{startDate} AND #{endDate}
AND tca.AGENCY_CD (+)= tms.AGENCY_CD AND tms.STORE_CD (+)= tci.STORE_CD ORDER BY tci.STORE_CD, tci.POS_NO

/* InstlManageMapper.getInstlRequestList */
SELECT tms.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tms.STORE_NM, tmp.STORE_CD, tmp.POS_NO, tci.SEQ_NO, tca.P_AGENCY_CD, tci.AGENCY_CD, tca.AGENCY_NM, tms.SYS_STAT_FG, TO_CHAR(TO_DATE(tci.INST_REQ_DT ,'YYYYMMDDhh24miss'), 'YYYY/MM/DD hh24:mi:ss') AS INST_REQ_DT, tci.INST_REQ_ID, TO_CHAR(TO_DATE(tci.INST_INS_DT ,'YYYYMMDDhh24miss'), 'YYYY/MM/DD hh24:mi:ss') AS INST_INS_DT, tci.INST_INS_ID, tci.INST_FG, tci.INST_REASON, tci.REMARK
FROM TB_MS_POS tmp, TB_MS_STORE tms, TB_HQ_OFFICE tho, ( SELECT STORE_CD, POS_NO, MAX(SEQ_NO) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS SEQ_NO, MAX(AGENCY_CD) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS AGENCY_CD, MAX(INST_REQ_DT) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS INST_REQ_DT, MAX(INST_REQ_ID) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS INST_REQ_ID, MAX(INST_INS_DT) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS INST_INS_DT, MAX(INST_INS_ID) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS INST_INS_ID, MAX(INST_FG) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS INST_FG, MAX(INST_REASON) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS INST_REASON, MAX(REMARK) KEEP( DENSE_RANK FIRST ORDER BY SEQ_NO DESC ) AS REMARK FROM TB_CM_INSTLL GROUP BY STORE_CD , POS_NO ) tci, TB_CM_AGENCY tca WHERE tms.STORE_CD = tmp.STORE_CD AND tho.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tci.STORE_CD (+)= tmp.STORE_CD AND tci.POS_NO (+)= tmp.POS_NO AND tca.AGENCY_CD (+)= tci.AGENCY_CD AND tci.INST_FG IS NOT NULL
AND (tci.AGENCY_CD = #{agencyCd} OR tca.AGENCY_CD = #{agencyCd})
AND tci.AGENCY_CD LIKE '%'|| #{srchAgencyCd}||'%'
AND tca.AGENCY_NM LIKE '%'|| #{srchAgencyNm} ||'%'
AND tmp.STORE_CD LIKE '%' ||#{storeCd}|| '%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tca.AGENCY_CD = #{agencyCd}
AND tca.P_AGENCY_CD = #{agencyCd}
AND tci.INST_FG = #{instFg}
ORDER BY tms.HQ_OFFICE_CD, tmp.STORE_CD, tmp.POS_NO,tci.SEQ_NO DESC
