/* OperMapper.getSaleStoreList */ SELECT A.HQ_OFFICE_CD, A.HQ_OFFICE_NM , B.STORE_CD, B.STORE_NM, B.SYS_STAT_FG, B.OWNER_NM, B.BIZ_NO , C.NMCODE_NM SYS_STAT_NM , TO_CHAR(TO_DATE(B.SYS_OPEN_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SYS_OPEN_DATE , TO_CHAR(TO_DATE(SUBSTR(B.REG_DT, 1, 8), 'yyyymmdd'), 'yyyy-mm-dd') AS REG_DATE , B.AGENCY_CD , D.AGENCY_NM , B.VAN_CD , E.VAN_NM , F.POS_CNT , G.BILL_CNT , G.SALE_CNT , G.RTN_SALE_CNT , G.REAL_SALE_CNT , G.TOT_GUEST_CNT , G.TOT_DLVR_CNT , G.TOT_RESVE_CNT , G.TOT_REFUND_CNT , G.MIN_SALE_YM , G.MAX_SALE_YM , CASE WHEN G.MIN_SALE_DATE IS NOT NULL THEN TO_CHAR(TO_DATE(G.MIN_SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') ELSE '' END AS MIN_SALE_DATE , CASE WHEN G.MAX_SALE_DATE IS NOT NULL THEN TO_CHAR(TO_DATE(G.MAX_SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') ELSE '' END AS MAX_SALE_DATE , H.P_BILL_CNT , H.P_SALE_CNT , H.P_RTN_SALE_CNT , H.P_REAL_SALE_CNT , I.P_CARD_CNT , J.P_CASH_CNT , J.P_CASH_APPR_CNT , K.VENDOR_TERMNL_NO , DECODE(L.SOLBI_STORE_CD, NULL, '신규', '전환') AS SHOP_MIG_FG FROM TB_HQ_OFFICE A , TB_MS_STORE B , TB_CM_NMCODE C , TB_CM_AGENCY D , TB_CM_VAN_CMPNY E , ( SELECT STORE_CD , COUNT(POS_NO) AS POS_CNT FROM TB_MS_POS WHERE USE_YN = 'Y' GROUP BY STORE_CD ) F , ( SELECT A.STORE_CD STORE_CD , A.BILL_CNT BILL_CNT , A.SALE_CNT SALE_CNT , A.RTN_SALE_CNT RTN_SALE_CNT , A.REAL_SALE_CNT REAL_SALE_CNT , A.TOT_GUEST_CNT TOT_GUEST_CNT , A.TOT_DLVR_CNT TOT_DLVR_CNT , A.TOT_RESVE_CNT TOT_RESVE_CNT , A.TOT_REFUND_CNT TOT_REFUND_CNT , A.MIN_SALE_YM MIN_SALE_YM , A.MAX_SALE_YM MAX_SALE_YM , MIN(B.SALE_DATE) MIN_SALE_DATE , MAX(C.SALE_DATE) MAX_SALE_DATE FROM ( SELECT A.STORE_CD , SUM(NVL(A.BILL_CNT , 0)) BILL_CNT , SUM(NVL(A.SALE_CNT , 0)) SALE_CNT , SUM(NVL(A.RTN_SALE_CNT , 0)) RTN_SALE_CNT , SUM(NVL(A.REAL_SALE_CNT , 0)) REAL_SALE_CNT , SUM(NVL(A.TOT_GUEST_CNT , 0)) TOT_GUEST_CNT , SUM(NVL(A.TOT_DLVR_CNT , 0)) TOT_DLVR_CNT , SUM(NVL(A.TOT_RESVE_CNT , 0)) TOT_RESVE_CNT , SUM(NVL(A.TOT_REFUND_CNT, 0)) TOT_REFUND_CNT , MIN(A.SALE_YM) MIN_SALE_YM , MAX(A.SALE_YM) MAX_SALE_YM FROM TB_SL_MONTHLY_TOTAL A GROUP BY A.STORE_CD ) A , TB_SL_DAILY_TOTAL B , TB_SL_DAILY_TOTAL C WHERE B.STORE_CD (+) = A.STORE_CD AND B.SALE_DATE (+) LIKE A.MIN_SALE_YM||'%' AND C.STORE_CD (+) = A.STORE_CD AND C.SALE_DATE (+) LIKE A.MAX_SALE_YM||'%' GROUP BY A.STORE_CD , A.BILL_CNT , A.SALE_CNT , A.RTN_SALE_CNT , A.REAL_SALE_CNT , A.TOT_GUEST_CNT , A.TOT_DLVR_CNT , A.TOT_RESVE_CNT , A.TOT_REFUND_CNT , A.MIN_SALE_YM , A.MAX_SALE_YM ) G , ( SELECT A.STORE_CD , SUM(NVL(A.BILL_CNT , 0)) P_BILL_CNT , SUM(NVL(A.SALE_CNT , 0)) P_SALE_CNT , SUM(NVL(A.RTN_SALE_CNT , 0)) P_RTN_SALE_CNT , SUM(NVL(A.REAL_SALE_CNT , 0)) P_REAL_SALE_CNT FROM TB_SL_DAILY_TOTAL A WHERE A.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY A.STORE_CD ) H , ( SELECT HQ_OFFICE_CD , STORE_CD , SUM(DECODE(SALE_YN, 'Y', 1, 'N', -1, 0)) AS P_CARD_CNT FROM TB_SL_SALE_PAY_CARD WHERE SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY HQ_OFFICE_CD , STORE_CD ) I , ( SELECT HQ_OFFICE_CD , STORE_CD , COUNT(STORE_CD) AS CASH_CNT , SUM(DECODE(SALE_YN, 'Y', 1, 'N', -1, 0)) AS P_CASH_CNT , SUM(CASE WHEN APPR_DT IS NOT NULL THEN DECODE(SALE_YN, 'Y', 1, 'N', -1, 0) ELSE 0 END) AS P_CASH_APPR_CNT FROM TB_SL_SALE_PAY_CASH WHERE SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY HQ_OFFICE_CD , STORE_CD ) J , ( SELECT A.STORE_CD , REGEXP_REPLACE(LISTAGG(A.VENDOR_TERMNL_NO, ',') WITHIN GROUP(ORDER BY A.VENDOR_TERMNL_NO), '([^,]+)(,\1)*(,|$)', '\1\3') VENDOR_TERMNL_NO FROM TB_MS_POS_TERMNL A , TB_MS_POS B WHERE A.VENDOR_FG = '01' AND B.STORE_CD = A.STORE_CD AND B.USE_YN = 'Y' GROUP BY A.STORE_CD ) K , TB_MIG_DATA_MAPPING L WHERE 1 = 1 AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD
AND A.HQ_OFFICE_CD LIKE '%'|| #{hqOfficeCd} ||'%'
AND A.HQ_OFFICE_NM LIKE '%'|| #{hqOfficeNm} ||'%'
AND B.STORE_CD LIKE '%'|| #{storeCd} ||'%'
AND B.STORE_NM LIKE '%'|| #{storeNm} ||'%'
AND B.VAN_CD = #{manageVanCd}
AND B.BIZ_NO = #{bizNo}
AND DECODE(L.SOLBI_STORE_CD, NULL, '0', '1') = #{srchShopMigFg}
AND B.AGENCY_CD = #{srchAgencyCd}
AND B.AGENCY_CD IN (SELECT AGENCY_CD FROM TB_CM_AGENCY WHERE AGENCY_CD = #{agencyCd} OR P_AGENCY_CD = #{agencyCd})
AND C.NMCODE_GRP_CD (+) = '005' AND C.NMCODE_CD (+) = B.SYS_STAT_FG AND D.AGENCY_CD (+) = B.AGENCY_CD AND E.VAN_FG (+) = '01' AND E.VAN_CD (+) = B.VAN_CD AND F.STORE_CD (+) = B.STORE_CD AND G.STORE_CD (+) = B.STORE_CD AND H.STORE_CD (+) = B.STORE_CD AND I.STORE_CD (+) = B.STORE_CD AND J.STORE_CD (+) = B.STORE_CD AND K.STORE_CD (+) = B.STORE_CD AND L.SOLBI_STORE_CD (+) = B.STORE_CD ORDER BY A.HQ_OFFICE_CD, A.HQ_OFFICE_NM , B.STORE_CD, B.STORE_NM, B.SYS_STAT_FG

/* OperMapper.getAgencyAuthList */ SELECT tca.AGENCY_CD, tca.AGENCY_NM,
(CASE WHEN tca.P_AGENCY_CD = '00000' THEN '총판' WHEN tca.P_AGENCY_CD != '00000' THEN '대리점' ELSE '' END) AS AGENCY_FG,
(CASE WHEN tca.AGENCY_CD = #{orgnCd} THEN '자사' WHEN tca.P_AGENCY_CD = #{orgnCd} THEN '대리점' ELSE '' END) AS AGENCY_FG,
tca.OWNER_NM, 0 AS BUY_CNT, 0 AS SALE_CNT, NVL(tci.INST_CNT,0) AS INST_CNT, 0 AS REST_CNT, tca.USE_YN, TO_CHAR(TO_DATE(tca.REG_DT, 'YYYYMMDDHH24MISS'), 'yyyy-mm-dd') AS REG_DT, tca.REG_ID FROM TB_CM_AGENCY tca, ( SELECT tci.AGENCY_CD, COUNT(tci.STORE_CD) AS INST_CNT FROM TB_CM_INSTLL tci, TB_MS_STORE tms WHERE 1=1 AND tci.SEQ_NO = '1' AND tci.INST_FG = '1' AND tms.STORE_CD (+)= tci.STORE_CD AND tms.AGENCY_CD (+)= tci.AGENCY_CD
AND tms.VAN_CD = #{manageVanCd}
GROUP BY tci.AGENCY_CD ) tci WHERE 1=1 AND tca.AGENCY_CD != '00000' AND tca.REG_DT BETWEEN #{startDate} AND #{endDate}
AND (tca.AGENCY_CD = #{orgnCd} OR tca.P_AGENCY_CD = #{orgnCd})
AND tca.AGENCY_CD LIKE '%'|| #{srchAgencyCd} ||'%'
AND tca.AGENCY_NM LIKE '%'|| #{srchAgencyNm} ||'%'
AND tca.AGENCY_CD = #{orgnCd}
AND tca.P_AGENCY_CD = #{orgnCd}
AND tci.AGENCY_CD (+)= tca.AGENCY_CD ORDER BY tca.AGENCY_CD
