mapper.select:
/* OperMapper.getSaleStoreList */
SELECT tho.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, tms.AGENCY_CD, (SELECT AGENCY_NM FROM TB_CM_AGENCY WHERE AGENCY_CD = tms.AGENCY_CD) AS AGENCY_NM, NVL(tmp.POS_CNT,0) AS POS_CNT, CASE WHEN tci.MAX_INST_INS_DT !='' THEN TO_CHAR(TO_DATE(tci.MAX_INST_INS_DT, 'yyyymmdd'), 'yyyy-mm-dd') ELSE '' END AS MAX_INST_INS_DT, TO_CHAR(TO_DATE(tms.SYS_OPEN_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SYS_OPEN_DATE, tms.SYS_STAT_FG
FROM TB_MS_STORE tms, TB_HQ_OFFICE tho, ( SELECT STORE_CD, COUNT(POS_NO) AS POS_CNT FROM TB_MS_POS WHERE 1=1 GROUP BY STORE_CD ) tmp, ( SELECT STORE_CD, MAX(INST_INS_DT) AS MAX_INST_INS_DT FROM TB_CM_INSTLL GROUP BY STORE_CD ) tci WHERE 1=1 AND tms.REG_DT BETWEEN #{startDate} AND #{endDate}
AND tms.AGENCY_CD = #{srchAgencyCd}
AND tms.AGENCY_CD IN (SELECT AGENCY_CD FROM TB_CM_AGENCY WHERE AGENCY_CD = #{agencyCd} OR P_AGENCY_CD = #{agencyCd})
AND tho.HQ_OFFICE_CD LIKE '%'|| #{hqOfficeCd} ||'%'
AND tho.HQ_OFFICE_NM LIKE '%'|| #{hqOfficeNm} ||'%'
AND tms.STORE_CD LIKE '%'|| #{storeCd} ||'%'
AND tms.STORE_NM LIKE '%'|| #{storeNm} ||'%'
AND tms.VAN_CD = #{manageVanCd}
AND tho.HQ_OFFICE_CD (+)= tms.HQ_OFFICE_CD AND tmp.STORE_CD (+)= tms.STORE_CD AND tci.STORE_CD (+)= tms.STORE_CD ORDER BY tho.HQ_OFFICE_CD, tms.STORE_CD

/* OperMapper.getAgencyAuthList */ SELECT tca.AGENCY_CD, tca.AGENCY_NM,
(CASE WHEN tca.P_AGENCY_CD = '00000' THEN '총판' WHEN tca.P_AGENCY_CD != '00000' THEN '대리점' ELSE '' END) AS AGENCY_FG,
(CASE WHEN tca.AGENCY_CD = #{orgnCd} THEN '자사' WHEN tca.P_AGENCY_CD = #{orgnCd} THEN '대리점' ELSE '' END) AS AGENCY_FG,
tca.OWNER_NM, 0 AS BUY_CNT, 0 AS SALE_CNT, NVL(tci.INST_CNT,0) AS INST_CNT, 0 AS REST_CNT, tca.USE_YN, TO_CHAR(TO_DATE(tca.REG_DT, 'YYYYMMDDHH24MISS'), 'yyyy-mm-dd') AS REG_DT, tca.REG_ID FROM TB_CM_AGENCY tca, ( SELECT tci.AGENCY_CD, COUNT(tci.STORE_CD) AS INST_CNT FROM TB_CM_INSTLL tci, TB_MS_STORE tms WHERE 1=1 AND tci.SEQ_NO = '1' AND tci.INST_FG = '1' AND tms.STORE_CD (+)= tci.STORE_CD AND tms.AGENCY_CD (+)= tci.AGENCY_CD
AND tms.VAN_CD = #{manageVanCd}
GROUP BY tci.AGENCY_CD ) tci WHERE 1=1 AND tca.AGENCY_CD != '00000' AND tca.REG_DT BETWEEN #{startDate} AND #{endDate}
AND (tca.AGENCY_CD = #{orgnCd} OR tca.P_AGENCY_CD = #{orgnCd})
AND tca.AGENCY_CD LIKE '%'|| #{srchAgencyCd} ||'%'
AND tca.AGENCY_NM LIKE '%'|| #{srchAgencyNm} ||'%'
AND tca.AGENCY_CD = #{orgnCd}
AND tca.P_AGENCY_CD = #{orgnCd}
AND tci.AGENCY_CD (+)= tca.AGENCY_CD ORDER BY tca.AGENCY_CD
