/* USE : OrderTimeTrackingMenuMapper.getOrderTimeTrackingMenuList */
SELECT tpot.HQ_OFFICE_CD , tpot.HQ_BRAND_CD , tpot.STORE_CD , tms.STORE_NM , tmsi.MOMS_STORE_FG_01 , tmsi.MOMS_STORE_FG_02 , tmsi.MOMS_STORE_FG_03 , tmsi.MOMS_STORE_FG_04 , tmsi.MOMS_STORE_FG_05 , TO_CHAR(TO_DATE(tpot.SALE_DATE,'yyyymmdd'),'yyyy-mm-dd')AS SALE_DATE , tpot.SERVICE_TYPE , tpot.CHANNEL_ORDER_NO , tpot.CHANNEL_TYPE , tpot.CHANNEL_SERVICE , TO_CHAR(TO_DATE(tpot.CHANNEL_REG_DT, 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS CHANNEL_REG_DT , tpot.VORDER_NO , TO_CHAR(TO_DATE(tpot.VORDER_REG_DT, 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS VORDER_REG_DT , tpot.ORDER_NO , C.PROD_CD , D.PROD_NM , TO_CHAR(TO_DATE(tpot.POS_REG_DT,'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS POS_REG_DT , tpot.ACCEPTED_FG , TO_CHAR(TO_DATE(tpot.ACCEPTED_DT, 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS ACCEPTED_DT , TO_CHAR(TO_DATE(tpot.COOKED_DT, 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS COOKED_DT , TO_CHAR(TO_DATE(tpot.FINISHED_DT, 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS FINISHED_DT , TO_CHAR(TO_DATE(DECODE(tpot.CALLED_DT, '1', '', tpot.CALLED_DT), 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS CALLED_DT , TO_CHAR(TO_DATE(DECODE(tpot.LAST_CALLED_DT, '1', '', tpot.LAST_CALLED_DT), 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS LAST_CALLED_DT , TO_CHAR(TO_DATE(tpot.ASSIGNED_DT, 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS ASSIGNED_DT , TO_CHAR(TO_DATE(tpot.PICKED_UP_DT, 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS PICKED_UP_DT , tpot.COMPLETED_FG , TO_CHAR(TO_DATE(tpot.COMPLETED_DT, 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS COMPLETED_DT , tpot.CANCELED_FG , TO_CHAR(TO_DATE(tpot.CANCELED_DT, 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH24:MI:SS') AS CANCELED_DT , tpot.CANCELED_MEMO
FROM TB_PS_ORDER_TRACKING tpot , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi , TB_OD_ORDER_DTL C , TB_MS_PRODUCT D WHERE tpot.HQ_OFFICE_CD = #{hqOfficeCd}
AND tpot.STORE_CD IN (${storeCdQuery})
AND tpot.SALE_DATE = #{startDate} AND tms.HQ_OFFICE_CD = tpot.HQ_OFFICE_CD AND tms.STORE_CD = tpot.STORE_CD AND tms.STORE_CD = tmsi.STORE_CD (+) AND C.STORE_CD (+) = tpot.STORE_CD AND C.SALE_DATE (+) = tpot.SALE_DATE AND C.ORDER_NO (+) = tpot.ORDER_NO AND D.STORE_CD (+) = C.STORE_CD AND D.PROD_CD (+) = C.PROD_CD
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tmsi.MOMS_STORE_FG_02 = #{momsStoreFg02}
AND tmsi.MOMS_STORE_FG_03 = #{momsStoreFg03}
AND tmsi.MOMS_STORE_FG_04 = #{momsStoreFg04}
AND tmsi.MOMS_STORE_FG_05 = #{momsStoreFg05}
ORDER BY tpot.CHANNEL_REG_DT DESC, tpot.POS_REG_DT DESC

