mapper.select:
		/* USE : SaleAnalsMonthlyMapper.getStSaleAnalsMonthlyList */

        SELECT	SUN, MON, TUE, WED, THU, FRI, SAT
		FROM	(
					SELECT	A.DT ||'||'|| NVL(B.TOT_SALE_AMT,0)||'||'||NVL(C.HOLIDAYYN,'N') AS DT, A.DW, A.WEEK
					FROM	(
								SELECT	TRUNC(DT + LEVEL-1, 'D') AS WEEK
										, TO_CHAR(DT + LEVEL-1, 'D') AS DW
										, TO_CHAR(DT + LEVEL-1,'yyyymmdd') AS DT
								FROM    (

											SELECT  TO_DATE(#{reqYearMonth}, 'yyyymm') DT
											FROM    DUAL
										)
								CONNECT BY LEVEL < = TO_CHAR(LAST_DAY(DT), 'dd')
							) A
							LEFT OUTER JOIN (
								SELECT	SALE_DATE AS DT
										, SUM(TOT_SALE_AMT) AS TOT_SALE_AMT
								FROM	TB_SL_DAILY_PROD
								WHERE	1=1
								AND HQ_OFFICE_CD = #{hqOfficeCd}


										AND HQ_BRAND_CD = #{hqBrandCd}


										AND STORE_CD = #{storeCd}


										AND SUBSTR(SALE_DATE,1,6) = #{reqYearMonth}


								GROUP BY SALE_DATE
							) B
							ON A.DT = B.DT
                            LEFT OUTER JOIN (
								SELECT	HOLIDAY_DT AS DT
                                        , 'Y' AS HOLIDAYYN
								FROM	TB_MS_HOLIDAY
								WHERE	1=1


										AND STORE_CD = #{storeCd}


										AND SUBSTR(HOLIDAY_DT,1,6) = #{reqYearMonth}


							) C
							ON A.DT = C.DT
				)
		PIVOT   (
					MAX(DT) FOR DW IN ('1' AS SUN, '2' AS MON, '3' AS TUE, '4' AS WED, '5' AS THU, '6' AS FRI, '7' AS SAT)
				)
		ORDER BY WEEK

SELECT TSSDP.SALE_DATE , TSSDP.POS_NO , TSSDP.BILL_NO , TSSDP.PAY_CD , ( SELECT NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '024' AND NMCODE_CD = TSSDP.PAY_CD ) AS PAY_CD_NM , SUM(TSSDP.PAY_AMT) AS PAY_AMT FROM TB_SL_SALE_HDR TSSH , TB_SL_SALE_DTL_PAY TSSDP WHERE TSSH.HQ_OFFICE_CD = TSSDP.HQ_OFFICE_CD AND TSSH.HQ_BRAND_CD = TSSDP.HQ_BRAND_CD AND TSSH.STORE_CD = TSSDP.STORE_CD AND TSSH.SALE_DATE = TSSDP.SALE_DATE AND TSSH.POS_NO = TSSDP.POS_NO AND TSSH.BILL_NO = TSSDP.BILL_NO AND TSSH.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSSDP.STORE_CD = #{storeCd}
AND TSSDP.SALE_DATE = #{saleDate}
GROUP BY TSSDP.SALE_DATE , TSSDP.POS_NO , TSSDP.BILL_NO , TSSDP.PAY_CD ORDER BY TSSDP.POS_NO, TSSDP.BILL_NO, TSSDP.PAY_CD
