mapper.select:
		/* USE : SaleAnalsMonthlyMomsMapper.getSaleAnalsMonthlyMomsList */

        SELECT	SUN, MON, TUE, WED, THU, FRI, SAT
		FROM	(
					SELECT	A.DT ||'||'|| NVL(B.REAL_SALE_AMT,0)||'||'||NVL(C.HOLIDAYYN,'N')||'||'|| NVL(B.STORE_CNT,0) AS DT, A.DW, A.WEEK
					FROM	(
								SELECT	TRUNC(DT + LEVEL-1, 'D') AS WEEK
										, TO_CHAR(DT + LEVEL-1, 'D') AS DW
										, TO_CHAR(DT + LEVEL-1,'yyyymmdd') AS DT
								FROM    (

											SELECT  TO_DATE(#{reqYearMonth}, 'yyyymm') DT
											FROM    DUAL
										)
								CONNECT BY LEVEL < = TO_CHAR(LAST_DAY(DT), 'dd')
							) A
							LEFT OUTER JOIN (
								SELECT	SALE_DATE AS DT
										, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT
										, COUNT(DISTINCT STORE_CD) AS STORE_CNT
								FROM	TB_SL_DAILY_TOTAL
								WHERE	1=1
								AND HQ_OFFICE_CD = #{hqOfficeCd}


										AND HQ_BRAND_CD = #{hqBrandCd}


										AND STORE_CD = #{storeCd}


			AND (
					'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
				OR	STORE_CD IN (SELECT STORE_CD  FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
				)


										AND SUBSTR(SALE_DATE,1,6) = #{reqYearMonth}


								GROUP BY SALE_DATE
							) B
							ON A.DT = B.DT
                            LEFT OUTER JOIN (
								SELECT	HOLIDAY_DT AS DT
                                        , 'Y' AS HOLIDAYYN
								FROM	TB_MS_HOLIDAY
								WHERE	1=1


										AND STORE_CD = #{storeCd}


			AND (
					'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
				OR	STORE_CD IN (SELECT STORE_CD  FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
				)


										AND SUBSTR(HOLIDAY_DT,1,6) = #{reqYearMonth}


							) C
							ON A.DT = C.DT
				)
		PIVOT   (
					MAX(DT) FOR DW IN ('1' AS SUN, '2' AS MON, '3' AS TUE, '4' AS WED, '5' AS THU, '6' AS FRI, '7' AS SAT)
				)
		ORDER BY WEEK

/* USE : SaleAnalsMonthlyMomsMapper.getSaleAnalsMonthlyMomsStoreList */ SELECT tsdt.STORE_CD , tms.STORE_NM , TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tsdt.BILL_CNT , tsdt.REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms WHERE tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD = #{storeCd}
AND tsdt.SALE_DATE = #{saleDate} ORDER BY tsdt.STORE_CD
