mapper.select:
/** StoreBrandMapper.getStoreBrandList */ SELECT TSDT.STORE_CD , TMS.STORE_NM , TMSI.INDEX_NO , NVL(CASE TMS.HQ_BRAND_CD WHEN '0000000' THEN '기본' ELSE (SELECT THB.HQ_BRAND_NM FROM TB_HQ_BRAND THB WHERE THB.HQ_OFFICE_CD = TMS.HQ_OFFICE_CD AND THB.HQ_BRAND_CD = TMS.HQ_BRAND_CD) END, '기타') AS HQ_BRAND_NM , SUM(TSDT.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TSDT.TOT_DC_AMT) AS TOT_DC_AMT , SUM(TSDT.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(TSDT.SALE_CNT) AS SALE_CNT , ROUND(RATIO_TO_REPORT(SUM(TSDT.REAL_SALE_AMT)) OVER() * 100, 2) AS RAT_REAL_SALE_AMT , ROUND(RATIO_TO_REPORT(SUM(TSDT.SALE_CNT)) OVER() * 100, 2) AS RAT_CNT FROM TB_MS_STORE TMS , TB_MS_STORE_INFO TMSI , TB_SL_DAILY_TOTAL TSDT WHERE TMS.STORE_CD = TSDT.STORE_CD AND TMSI.STORE_CD (+) = TMS.STORE_CD
AND TMS.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSDT.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND TSDT.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR TSDT.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
GROUP BY TSDT.STORE_CD, TMS.STORE_NM, TMSI.INDEX_NO, TMS.HQ_BRAND_CD, TMS.HQ_OFFICE_CD ORDER BY TMS.STORE_NM
