mapper.select:
SELECT TSDT.STORE_CD , TMS.STORE_NM , SUM(TSDT.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TSDT.TOT_DC_AMT) AS TOT_DC_AMT , SUM(TSDT.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(TSDT.SALE_CNT) AS SALE_CNT , ROUND(RATIO_TO_REPORT(SUM(TSDT.REAL_SALE_AMT)) OVER() *100, 1) AS RAT_REAL_SALE_AMT , ROUND(RATIO_TO_REPORT(SUM(TSDT.SALE_CNT)) OVER() *100, 1) AS RAT_CNT
FROM TB_MS_STORE TMS , TB_SL_DAILY_TOTAL TSDT WHERE TMS.STORE_CD = TSDT.STORE_CD
AND TSDT.SALE_DATE >= #{startDate}
AND TSDT.SALE_DATE <= #{endDate}
GROUP BY TSDT.STORE_CD, TMS.STORE_NM ORDER BY TMS.STORE_NM
