mapper.select:
/** StoreFgMapper.getStoreFgList */ SELECT TSDP.STORE_CD , TMS.STORE_NM , TMSI.INDEX_NO
, DECODE(TMS.direct_manage_yn,'Y','직영','N','가맹','기타') AS CLS_FG
, DECODE(TMS.CLS_FG,'1','외식','2','유통','기타') AS CLS_FG
, SUM(TSDP.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TSDP.TOT_DC_AMT) AS TOT_DC_AMT , SUM(TSDP.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(TSDP.TOT_SALE_QTY) AS SALE_CNT , ROUND(RATIO_TO_REPORT(SUM(TSDP.REAL_SALE_AMT)) OVER() * 100, 2) AS RAT_REAL_SALE_AMT , ROUND(RATIO_TO_REPORT(SUM(TSDP.TOT_SALE_QTY)) OVER() * 100, 2) AS RAT_CNT FROM TB_MS_STORE TMS , TB_MS_STORE_INFO TMSI , TB_SL_DAILY_PROD TSDP WHERE TMS.STORE_CD = TSDP.STORE_CD AND TMSI.STORE_CD (+) = TMS.STORE_CD
AND TMS.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSDP.PROD_CD = #{prodCd}
AND TSDP.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND TMS.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR TMS.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND EXISTS (SELECT * FROM TB_HQ_PRODUCT Z WHERE Z.HQ_OFFICE_CD = TSDP.HQ_OFFICE_CD AND Z.PROD_CD = TSDP.PROD_CD AND Z.PROD_CD = #{prodCd})
AND TMS.STORE_CD IN
#{item}
AND EXISTS (SELECT * FROM TB_MS_PRODUCT Z WHERE Z.STORE_CD = TSDP.STORE_CD AND Z.PROD_CD = TSDP.PROD_CD AND Z.PROD_CD = #{prodCd})
GROUP BY TSDP.STORE_CD, TMS.STORE_NM, TMSI.INDEX_NO, TMS.direct_manage_yn ORDER BY TMS.direct_manage_yn, SUM(TSDP.TOT_SALE_AMT) DESC
GROUP BY TSDP.STORE_CD, TMS.STORE_NM, TMSI.INDEX_NO, TMS.CLS_FG ORDER BY TMS.CLS_FG, SUM(TSDP.TOT_SALE_AMT) DESC
