mapper.select:
        /* USE : storeMonthMapper.getStoreRankList */		 
		SELECT M.RN
			 ${sQuery1}
		  FROM (
		      SELECT M.GBN
		      		, M.RN
				    ${sQuery2}
		        FROM (
					${sQuery3}
		             ) M
		       ) M
		GROUP BY M.RN

			ORDER BY M.RN ASC


		 	ORDER BY M.RN DESC

        /* USE : storeMonthMapper.getMonthColList */

        SELECT DISTINCT TO_CHAR(TO_DATE(TSDT.SALE_DATE), 'YYYYMM') AS MONTH 
        FROM TB_SL_DAILY_TOTAL TSDT
        WHERE 1 = 1
		AND TO_CHAR(TO_DATE(TSDT.SALE_DATE), 'YYYYMM') >= #{startDate}
        AND TO_CHAR(TO_DATE(TSDT.SALE_DATE), 'YYYYMM') <= #{endDate}   
        ORDER BY MONTH
