mapper.select:
SELECT M.LV , SUM(M.REAL_SALE_AMT_A) AS REAL_SALE_AMT_A , SUM(M.SALE_CNT_A) AS SALE_CNT_A , SUM(M.REAL_SALE_AMT_B) AS REAL_SALE_AMT_B , SUM(M.SALE_CNT_B) AS SALE_CNT_B , CASE WHEN (SUM(M.REAL_SALE_AMT_A)-SUM(M.REAL_SALE_AMT_B)) != 0 THEN ROUND((SUM(M.REAL_SALE_AMT_A)-SUM(M.REAL_SALE_AMT_B))/ SUM(M.REAL_SALE_AMT_B)*100,1) ELSE 0 END AS SIN_AMT , CASE WHEN (SUM(M.SALE_CNT_A)-SUM(M.SALE_CNT_B)) != 0 THEN ROUND((SUM(M.SALE_CNT_A)-SUM(M.SALE_CNT_B))/ SUM(M.SALE_CNT_B)*100,1) ELSE 0 END AS SIN_CNT
FROM ( SELECT M.LV , DECODE(M.GBN,'A',M.SALE_HOUR,'') SALE_HOUR_A , DECODE(M.GBN,'A',M.REAL_SALE_AMT,'') REAL_SALE_AMT_A , DECODE(M.GBN,'A',M.SALE_CNT,'') SALE_CNT_A , DECODE(M.GBN,'B',M.SALE_HOUR,'') SALE_HOUR_B , DECODE(M.GBN,'B',M.REAL_SALE_AMT,'') REAL_SALE_AMT_B , DECODE(M.GBN,'B',M.SALE_CNT,'') SALE_CNT_B FROM ( SELECT M.LV, M.GBN, M.SALE_HOUR, M.REAL_SALE_AMT, M.SALE_CNT FROM ( SELECT LV.LV, NVL(A.GBN,'A') AS GBN, NVL(A.SALE_HOUR,LV.LV) AS SALE_HOUR, NVL(A.REAL_SALE_AMT,0) AS REAL_SALE_AMT, NVL(A.SALE_CNT, 0) AS SALE_CNT FROM ( SELECT LPAD(LEVEL-1, 2, '0') LV
FROM DUAL CONNECT BY LEVEL <= 24
) LV , ( SELECT 'A' AS GBN , TSDT.SALE_HOUR , SUM(TSDT.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(TSDT.SALE_CNT) AS SALE_CNT FROM TB_SL_DAILY_TIME TSDT WHERE 1=1
AND TSDT.STORE_CD IN
#{item}
AND TSDT.SALE_DATE >= #{startDate}
AND TSDT.SALE_DATE <= #{endDate}
GROUP BY TSDT.SALE_HOUR ) A WHERE LV.LV = A.SALE_HOUR(+) ORDER BY LV ) M UNION ALL SELECT M.LV, M.GBN, M.SALE_HOUR, M.REAL_SALE_AMT, M.SALE_CNT FROM ( SELECT LV.LV, NVL(A.GBN,'B') AS GBN, NVL(A.SALE_HOUR,LV.LV) AS SALE_HOUR, NVL(A.REAL_SALE_AMT,0) AS REAL_SALE_AMT, NVL(A.SALE_CNT, 0) AS SALE_CNT FROM ( SELECT LPAD(LEVEL-1, 2, '0') LV
FROM DUAL CONNECT BY LEVEL <= 24
) LV , ( SELECT 'B' AS GBN , TSDT.SALE_HOUR , SUM(TSDT.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(TSDT.SALE_CNT) AS SALE_CNT FROM TB_SL_DAILY_TIME TSDT WHERE 1=1
AND TSDT.STORE_CD IN
#{item}
AND TSDT.SALE_DATE >= #{startDate}
AND TSDT.SALE_DATE <= #{endDate}
GROUP BY TSDT.SALE_HOUR ) A WHERE LV.LV = A.SALE_HOUR(+) ORDER BY LV ) M ) M ) M GROUP BY M.LV ORDER BY M.LV
