mapper.select:
/* GiftMapper.getGiftList */
SELECT TO_CHAR(TO_DATE(tsspg.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsspg.STORE_CD , tms.STORE_NM , tsspg.POS_NO , tsspg.BILL_NO , tsspg.SALE_FG -- 상품권명 -- 상품권번호 -- 상품권금액 , tsspg.SALE_AMT AS APPR_AMT -- 승인번호 -- 승인일시
FROM TB_SL_SALE_PAY_GIFT tsspg , TB_MS_STORE tms WHERE tsspg.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsspg.STORE_CD = tms.STORE_CD AND tsspg.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspg.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsspg.STORE_CD IN
#{item}
AND tms.HQ_BRAND_CD = #{hqBrandCd}
ORDER BY tsspg.SALE_DATE, tsspg.STORE_CD, tsspg.POS_NO, tsspg.BILL_NO

/* GiftMapper.getGiftExcelList */ SELECT TO_CHAR(TO_DATE(tsspg.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsspg.STORE_CD , tms.STORE_NM , tsspg.POS_NO , tsspg.BILL_NO , tsspg.SALE_FG -- 상품권명 -- 상품권번호 -- 상품권금액 , tsspg.SALE_AMT AS APPR_AMT -- 승인번호 -- 승인일시 FROM TB_SL_SALE_PAY_GIFT tsspg , TB_MS_STORE tms WHERE tsspg.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsspg.STORE_CD = tms.STORE_CD AND tsspg.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspg.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsspg.STORE_CD IN
#{item}
AND tms.HQ_BRAND_CD = #{hqBrandCd}
ORDER BY tsspg.SALE_DATE, tsspg.STORE_CD, tsspg.POS_NO, tsspg.BILL_NO
