mapper.select:
/* AreaMapper.getAreaList */
SELECT TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsdt.STORE_CD , tms.STORE_NM , tms.AREA_CD , tsdt.BILL_CNT , DECODE(tsdt.BILL_CNT, 0, 0, ROUND(tsdt.TOT_SALE_AMT/tsdt.BILL_CNT)) AS BILL_UPRC , tsdt.TOT_GUEST_CNT , DECODE(tsdt.TOT_GUEST_CNT, 0, 0, ROUND(tsdt.TOT_SALE_AMT/tsdt.TOT_GUEST_CNT)) AS GUEST_UPRC , tsdt.SALE_CNT , tsdt.TOT_SALE_AMT , tsdt.REAL_SALE_AMT , tsdt.TOT_DC_AMT , tsdt.PAY_AMT_02 AS CASH_ATM , tsdt.PAY_AMT_01 AS CARD_AMT , (tsdt.PAY_AMT_03 + tsdt.PAY_AMT_04 + tsdt.PAY_AMT_05 + tsdt.PAY_AMT_06 + tsdt.PAY_AMT_07 + tsdt.PAY_AMT_08 + tsdt.PAY_AMT_09 + tsdt.PAY_AMT_10 + tsdt.PAY_AMT_11 + tsdt.PAY_AMT_12 + tsdt.PAY_AMT_13 + tsdt.PAY_AMT_14 + tsdt.PAY_AMT_15 + tsdt.PAY_AMT_16 + tsdt.PAY_AMT_17 + tsdt.PAY_AMT_18 + tsdt.PAY_AMT_19 + tsdt.PAY_AMT_20) AS ETC_AMT
FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms WHERE tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdt.STORE_CD IN
#{item}
ORDER BY tsdt.SALE_DATE, tsdt.STORE_CD

