mapper.select:
/* CardCreditMapper.getStoreCdComboList */
SELECT
tms.STORE_CD AS VALUE,
tms.STORE_NM AS NAME
FROM TB_MS_STORE tms
WHERE 1=1
AND tms.HQ_OFFICE_CD = #{hqOfficeCd}

    AND tms.STORE_CD = #{storeCd}

ORDER BY tms.STORE_CD

/* CardCreditMapper.getCardCreditList */ SELECT tsspc.HQ_OFFICE_CD, tsspc.HQ_BRAND_CD, tsspc.STORE_CD, tms.STORE_NM, (CASE WHEN tsspc.SALE_DATE IS NOT NULL THEN SUBSTR(tsspc.SALE_DATE, 1, 4)||'-'||SUBSTR(tsspc.SALE_DATE, 5, 2)||'-'||SUBSTR(tsspc.SALE_DATE, 7, 2) ELSE tsspc.SALE_DATE END) AS SALE_DATE, tsspc.POS_NO, tsspc.BILL_NO, tsspc.LINE_NO, tsspc.LINE_SEQ_NO, (tsspc.POS_NO ||'-'|| tsspc.BILL_NO ||'-'|| tsspc.LINE_NO ||'-'|| tsspc.LINE_SEQ_NO) AS BILL, (CASE WHEN tsspc.APPR_DT IS NOT NULL THEN SUBSTR(tsspc.APPR_DT, 1, 4)||'-'||SUBSTR(tsspc.APPR_DT, 5, 2)||'-'||SUBSTR(tsspc.APPR_DT, 7, 2) ELSE tsspc.APPR_DT END) AS APPR_DATE, tsspc.APPR_NO, (tsspc.SALE_FG * tsspc.APPR_AMT) AS APPR_AMT, DECODE(tsspc.SALE_FG, 1, '승인', -1, '취소') AS APPR_GUBUN, tsspc.CARD_NO, tsspc.ACQUIRE_NM, tsspc.MEMBR_JOIN_NO, DECODE(tsspc.INST_CNT, 0 , '일시불', '할부') AS INST_CNT_NM, (CASE WHEN tsspcck.CREDIT_DATE IS NOT NULL THEN SUBSTR(tsspcck.CREDIT_DATE, 1, 4)||'-'||SUBSTR(tsspcck.CREDIT_DATE, 5, 2)||'-'||SUBSTR(tsspcck.CREDIT_DATE, 7, 2) ELSE tsspcck.CREDIT_DATE END) AS CREDIT_DATE, tsspcck.CREDIT_AMT, tsspcck.CREDIT_FEE, tsspcck.CREDIT_BANK, (CASE WHEN (tsspc.SALE_FG = 1 AND tssh.ORG_BILL_NO IS NOT NULL) THEN '취소된승인' WHEN (tsspc.SALE_FG = 1 AND tssh.ORG_BILL_NO IS NULL) THEN '승인' WHEN tsspc.SALE_FG = -1 THEN '취소' ELSE null END ) AS APPR_GUBUN2 FROM TB_SL_SALE_PAY_CARD tsspc, TB_SL_SALE_PAY_CARD_CREDIT_KWU tsspcck, TB_MS_STORE tms, TB_SL_SALE_HDR tssh WHERE 1=1 AND tsspc.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspc.STORE_CD = #{storeCd} AND SUBSTR(tsspc.APPR_DT, 0, 8) BETWEEN #{startDate} AND #{endDate} AND tsspc.POS_NO NOT IN ('99') AND tsspcck.HQ_OFFICE_CD (+)= tsspc.HQ_OFFICE_CD AND tsspcck.HQ_BRAND_CD (+)= tsspc.HQ_BRAND_CD AND tsspcck.STORE_CD (+)= tsspc.STORE_CD AND tsspcck.SALE_DATE (+)= tsspc.SALE_DATE AND tsspcck.POS_NO (+)= tsspc.POS_NO AND tsspcck.BILL_NO (+)= tsspc.BILL_NO AND tsspcck.LINE_NO (+)= tsspc.LINE_NO AND tsspcck.LINE_SEQ_NO (+)= tsspc.LINE_SEQ_NO AND tms.HQ_OFFICE_CD = tsspc.HQ_OFFICE_CD AND tms.STORE_CD = tsspc.STORE_CD
AND tssh.ORG_BILL_NO IS NULL
AND tssh.HQ_OFFICE_CD = tsspc.HQ_OFFICE_CD AND tssh.HQ_BRAND_CD = tsspc.HQ_BRAND_CD AND tssh.STORE_CD = tsspc.STORE_CD AND tssh.SALE_DATE = tsspc.SALE_DATE AND tssh.POS_NO = tsspc.POS_NO AND tssh.BILL_NO = tsspc.BILL_NO ORDER BY tsspc.STORE_CD, tsspc.SALE_DATE, tsspc.POS_NO, tsspc.BILL_NO, tsspc.LINE_NO, tsspc.LINE_SEQ_NO

/* CardCreditMapper.getCardCreditExcelSampleList */ SELECT tsspc.HQ_OFFICE_CD, tsspc.HQ_BRAND_CD, tsspc.STORE_CD, tms.STORE_NM, tsspc.SALE_DATE, tsspc.POS_NO, tsspc.BILL_NO, tsspc.LINE_NO, tsspc.LINE_SEQ_NO, (tsspc.POS_NO ||'-'|| tsspc.BILL_NO ||'-'|| tsspc.LINE_NO ||'-'|| tsspc.LINE_SEQ_NO) AS BILL, SUBSTR(tsspc.APPR_DT, 0, 8) AS APPR_DATE, tsspc.APPR_NO, (tsspc.SALE_FG * tsspc.APPR_AMT) AS APPR_AMT, DECODE(tsspc.SALE_FG, 1, '승인', -1, '취소') AS APPR_GUBUN, tsspc.CARD_NO, tsspc.ACQUIRE_NM, tsspc.MEMBR_JOIN_NO, DECODE(tsspc.INST_CNT, 0 , '일시불', '할부') AS INST_CNT_NM, tsspcck.CREDIT_DATE, tsspcck.CREDIT_AMT, tsspcck.CREDIT_FEE, tsspcck.CREDIT_BANK, (CASE WHEN (tsspc.SALE_FG = 1 AND tssh.ORG_BILL_NO IS NOT NULL) THEN '취소된승인' WHEN (tsspc.SALE_FG = 1 AND tssh.ORG_BILL_NO IS NULL) THEN '승인' WHEN tsspc.SALE_FG = -1 THEN '취소' ELSE null END ) AS APPR_GUBUN2 FROM TB_SL_SALE_PAY_CARD tsspc, TB_SL_SALE_PAY_CARD_CREDIT_KWU tsspcck, TB_MS_STORE tms, TB_SL_SALE_HDR tssh WHERE 1=1 AND tsspc.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspc.STORE_CD = #{storeCd} AND SUBSTR(tsspc.APPR_DT, 0, 8) BETWEEN #{startDate} AND #{endDate} AND tsspc.POS_NO NOT IN ('99') AND tsspcck.HQ_OFFICE_CD (+)= tsspc.HQ_OFFICE_CD AND tsspcck.HQ_BRAND_CD (+)= tsspc.HQ_BRAND_CD AND tsspcck.STORE_CD (+)= tsspc.STORE_CD AND tsspcck.SALE_DATE (+)= tsspc.SALE_DATE AND tsspcck.POS_NO (+)= tsspc.POS_NO AND tsspcck.BILL_NO (+)= tsspc.BILL_NO AND tsspcck.LINE_NO (+)= tsspc.LINE_NO AND tsspcck.LINE_SEQ_NO (+)= tsspc.LINE_SEQ_NO AND tms.HQ_OFFICE_CD = tsspc.HQ_OFFICE_CD AND tms.STORE_CD = tsspc.STORE_CD
AND tssh.ORG_BILL_NO IS NULL
AND tssh.HQ_OFFICE_CD = tsspc.HQ_OFFICE_CD AND tssh.HQ_BRAND_CD = tsspc.HQ_BRAND_CD AND tssh.STORE_CD = tsspc.STORE_CD AND tssh.SALE_DATE = tsspc.SALE_DATE AND tssh.POS_NO = tsspc.POS_NO AND tssh.BILL_NO = tsspc.BILL_NO ORDER BY tsspc.STORE_CD, tsspc.SALE_DATE, tsspc.POS_NO, tsspc.BILL_NO, tsspc.LINE_NO, tsspc.LINE_SEQ_NO

mapper.delete:
/* CardCreditMapper.getCardCreditExcelUploadAddDeleteAll */
DELETE
TB_TMP_EXCEL_CARD_CREDIT_KWU
WHERE 1=1
AND SESSION_ID = #{sessionId}
AND HQ_OFFICE_CD = #{hqOfficeCd}


        AND STORE_CD = #{storeCd}
