mapper.select:
/* DayBillInfoMapper.getDayStoreBillList */ SELECT A.STORE_CD, A.STORE_NM, A.REAL_SALE_AMT, A.SALE_CNT, A.RTN_SALE_CNT, A.BILL_CNT, ROUND( A.REAL_SALE_AMT / DECODE(A.BILL_CNT, 0, null, A.BILL_CNT) ) AS BILL_UPRC, A.TOT_GUEST_CNT, ROUND( A.REAL_SALE_AMT / DECODE(A.TOT_GUEST_CNT, 0, null, A.TOT_GUEST_CNT) ) AS GUEST_UPRC FROM ( SELECT tssh.STORE_CD, (SELECT STORE_NM FROM TB_MS_STORE WHERE STORE_CD = tssh.STORE_CD) AS STORE_NM, SUM(tssh.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(CASE WHEN SALE_FG = '1' THEN 1 ELSE 0 END) AS SALE_CNT, SUM(CASE WHEN SALE_FG = '-1' THEN -1 ELSE 0 END) AS RTN_SALE_CNT, SUM(CASE WHEN SALE_FG = '1' THEN 1 ELSE 0 END) + SUM(CASE WHEN SALE_FG = '-1' THEN -1 ELSE 0 END) AS BILL_CNT, SUM(tssh.TOT_GUEST_CNT) AS TOT_GUEST_CNT FROM TB_SL_SALE_HDR tssh WHERE 1=1 AND tssh.HQ_OFFICE_CD = #{membrOrgnCd}
AND tssh.SALE_DATE = #{saleDate}
AND tssh.SALE_DATE BETWEEN #{yearMonth}||'01' AND #{yearMonth} ||'31'
AND tssh.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{membrOrgnCd} AND ENVST_CD = '0001') OR tssh.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) ) GROUP BY tssh.HQ_OFFICE_CD, tssh.STORE_CD ORDER BY tssh.HQ_OFFICE_CD, tssh.STORE_CD ) A

/* DayBillInfoMapper.getDayStoreBillList2 */ SELECT tssh.STORE_CD, (SELECT STORE_NM FROM TB_MS_STORE WHERE STORE_CD = tssh.STORE_CD) AS STORE_NM, tssh.POS_NO, tssh.BILL_NO, TO_CHAR(TO_DATE(tssh.BILL_DT, 'YYYY-MM-DD HH24:MI:SS'),'yyyy-mm-dd hh24:mi:ss') AS BILL_DT, tssh.SALE_YN, tssh.TOT_SALE_AMT, tssh.TOT_DC_AMT, tssh.REAL_SALE_AMT FROM TB_SL_SALE_HDR tssh WHERE 1=1 AND tssh.HQ_OFFICE_CD = #{membrOrgnCd}
AND tssh.SALE_DATE = #{saleDate}
AND tssh.SALE_DATE BETWEEN #{yearMonth}||'01' AND #{yearMonth} ||'31'
AND
tssh.STORE_CD = #{item}
ORDER BY tssh.HQ_OFFICE_CD, tssh.STORE_CD, tssh.BILL_DT, tssh.POS_NO
