mapper.select:
/* USE : DayPayInfoMapper.getDayCardList */
SELECT NVL(tsspc.ACQUIRE_CD, '9999') AS CARD_CD, NVL(tsspc.ACQUIRE_NM, '기타') AS CARD_NM , SUM(DECODE(tsspc.SALE_YN, 'Y', 1, 0)) AS APPR_CNT , SUM(DECODE(tsspc.SALE_YN, 'Y', tsspc.APPR_AMT, 0)) AS APPR_AMT , SUM(DECODE(tsspc.SALE_YN, 'N', 1, 0)) AS CANCEL_CNT , SUM(DECODE(tsspc.SALE_YN, 'N', tsspc.SALE_AMT, 0)) AS CANCEL_AMT FROM TB_SL_SALE_PAY_CARD tsspc WHERE tsspc.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsspc.SALE_DATE = #{saleDate}
AND tsspc.SALE_DATE BETWEEN #{yearMonth}||'01' AND #{yearMonth} ||'31'
AND tsspc.STORE_CD IN
#{item}
GROUP BY NVL(tsspc.ACQUIRE_CD, '9999'), NVL(tsspc.ACQUIRE_NM, '기타') ORDER BY NVL(tsspc.ACQUIRE_CD, '9999')

/* USE : PayInfoMapper.getDayCashList */
SELECT tsspc.STORE_CD, tms.STORE_NM, tsspc.CORNR_CD, tmc.CORNR_NM , tsspc.APPR_PROC_FG --, tcn.NMCODE_NM AS APPR_PROC_NM , DECODE(tsspc.APPR_PROC_FG, 1, '포스승인', 2, '단말기승인') AS APPR_PROC_NM , tsspc.CASH_BILL_CARD_NO , tsspc.APPR_NO , tsspc.SALE_AMT AS APPR_AMT , SUBSTR(tsspc.APPR_DT, 0, 8) AS APPR_DATE , SUBSTR(tsspc.APPR_DT, 9, 14) AS APPR_TIME , tsspc.VAN_CD, tcvc.VAN_NM FROM TB_SL_SALE_PAY_CASH tsspc , TB_MS_CORNER tmc , TB_CM_VAN_CMPNY tcvc , TB_CM_NMCODE tcn , TB_MS_STORE tms WHERE tsspc.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsspc.SALE_DATE = #{saleDate}
AND tsspc.SALE_DATE BETWEEN #{yearMonth}||'01' AND #{yearMonth} ||'31'
AND tsspc.STORE_CD IN
#{item}
AND tmc.STORE_CD (+) = tsspc.STORE_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tmc.CORNR_CD (+) = tsspc.CORNR_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tcvc.VAN_FG (+) = '01' /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tcvc.VAN_CD (+) = tsspc.VAN_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tcn.NMCODE_GRP_CD (+) = '018' AND tcn.NMCODE_CD (+) = tsspc.APPR_PROC_FG AND tms.HQ_OFFICE_CD = tsspc.HQ_OFFICE_CD AND tms.STORE_CD = tsspc.STORE_CD
AND tsspc.APPR_PROC_FG <> '3'
AND tsspc.APPR_PROC_FG = '3'
ORDER BY tsspc.STORE_CD, tms.STORE_NM, tsspc.CORNR_CD, tmc.CORNR_NM, tsspc.APPR_DT

/* USE : PayInfoMapper.getDayPaycoList */
SELECT tsspp.APPR_COMPANY_NM AS PAYCO_NM , SUM(DECODE(tsspp.SALE_YN, 'Y', 1, 0)) AS APPR_CNT , SUM(DECODE(tsspp.SALE_YN, 'Y', tsspp.APPR_AMT+tsspp.POINT_AMT, 0)) AS APPR_AMT , SUM(DECODE(tsspp.SALE_YN, 'N', 1, 0)) AS CANCEL_CNT , SUM(DECODE(tsspp.SALE_YN, 'N', tsspp.APPR_AMT+tsspp.POINT_AMT, 0)) AS CANCEL_AMT FROM TB_SL_SALE_PAY_PAYCO tsspp WHERE tsspp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsspp.SALE_DATE = #{saleDate}
AND tsspp.SALE_DATE BETWEEN #{yearMonth}||'01' AND #{yearMonth} ||'31'
AND tsspp.STORE_CD IN
#{item}
GROUP BY tsspp.APPR_COMPANY_NM ORDER BY tsspp.APPR_COMPANY_NM

