/* USE : PayInfoMapper.getDayMpayList */
SELECT tsspm.MPAY_CD, tcn.NMCODE_NM AS MPAY_NM , SUM(DECODE(tsspm.SALE_YN, 'Y', 1, 0)) AS APPR_CNT , SUM(DECODE(tsspm.SALE_YN, 'Y', tsspm.APPR_AMT, 0)) AS APPR_AMT , SUM(DECODE(tsspm.SALE_YN, 'N', 1, 0)) AS CANCEL_CNT , SUM(DECODE(tsspm.SALE_YN, 'N', tsspm.APPR_AMT, 0)) AS CANCEL_AMT FROM TB_SL_SALE_PAY_MPAY tsspm , TB_CM_NMCODE tcn WHERE tsspm.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsspm.SALE_DATE = #{saleDate}
AND tsspm.SALE_DATE BETWEEN #{yearMonth}||'01' AND #{yearMonth} ||'31'
