AND tsspp.SALE_DATE = #{saleDate}
AND tsspp.SALE_DATE BETWEEN #{yearMonth}||'01' AND #{yearMonth} ||'31'
AND tsspp.STORE_CD IN
#{item}
AND tmc.STORE_CD (+) = tsspp.STORE_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tmc.CORNR_CD (+) = tsspp.CORNR_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tmm.MEMBR_ORGN_CD (+) = #{orgnGrpCd} /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tmm.MEMBR_NO (+) = tsspp.MEMBR_NO /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tmmc.MEMBR_ORGN_CD (+) = #{orgnGrpCd} /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tmmc.MEMBR_CLASS_CD (+) = tmm.MEMBR_CLASS_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tcn.NMCODE_GRP_CD (+) = '054' /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tcn.NMCODE_CD (+) = tmmc.POINT_SAVE_FG /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ GROUP BY tsspp.MEMBR_NO, tmm.MEMBR_NM , tmm.MEMBR_CLASS_CD, tmmc.MEMBR_CLASS_NM , tmmc.POINT_SAVE_FG, tcn.NMCODE_NM , tmmc.DC_RATE

