/* USE : PayInfoMapper.getDayGiftList */
SELECT tsspgd.SALE_DATE , tsspgd.STORE_CD, tms.STORE_NM , tsspgd.POS_NO, tsspgd.BILL_NO , tsspgd.SALE_YN , tsspgd.GIFT_CD, tmg.GIFT_NM , tsspgd.GIFT_PROC_FG, tcn.NMCODE_NM AS GIFT_PROC_FG_NM , tsspgd.GIFT_SER_NO , tsspgd.GIFT_UPRC , tsspgd.GIFT_QTY , tsspgd.GIFT_UPRC * tsspgd.GIFT_QTY AS GIFT_TOT_AMT , tsspgr.RTN_PAY_AMT FROM TB_SL_SALE_PAY_GIFT_DTL tsspgd , TB_SL_SALE_PAY_GIFT_RTN tsspgr , TB_MS_GIFT tmg , TB_CM_NMCODE tcn , TB_MS_STORE tms WHERE tsspgd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsspgd.SALE_DATE = #{saleDate}
AND tsspgd.SALE_DATE BETWEEN #{yearMonth}||'01' AND #{yearMonth} ||'31'
AND tsspgd.STORE_CD IN
#{item}
AND tsspgr.HQ_OFFICE_CD (+) = tsspgd.HQ_OFFICE_CD AND tsspgr.STORE_CD (+) = tsspgd.STORE_CD AND tsspgr.SALE_DATE (+) = tsspgd.SALE_DATE AND tsspgr.POS_NO (+) = tsspgd.POS_NO AND tsspgr.BILL_NO (+) = tsspgd.BILL_NO AND tsspgr.LINE_NO (+) = tsspgd.LINE_NO AND tmg.STORE_CD (+) = tsspgd.STORE_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tmg.GIFT_CD (+) = tsspgd.GIFT_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tcn.NMCODE_GRP_CD = '048' AND tcn.NMCODE_CD = tsspgd.GIFT_PROC_FG AND tms.STORE_CD (+) = tsspgd.STORE_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ ORDER BY tsspgd.SALE_DATE, tsspgd.STORE_CD , tsspgd.POS_NO, tsspgd.BILL_NO , tsspgd.GIFT_SEQ

