/* USE : PayInfoMapper.getDayPartnerList */
SELECT tsspp.PARTN_CD, tmp.PARTN_NM , SUM(DECODE(tsspp.SALE_YN, 'Y', 1, 0)) AS SALE_CNT , SUM(DECODE(tsspp.SALE_YN, 'Y', tsspp.SALE_AMT, 0)) AS SALE_AMT , SUM(DECODE(tsspp.SALE_YN, 'Y', tsspp.DC_AMT, 0)) AS DC_AMT , SUM(DECODE(tsspp.SALE_YN, 'N', 1, 0)) AS CANCEL_CNT , SUM(DECODE(tsspp.SALE_YN, 'N', tsspp.SALE_AMT, 0)) AS CANCEL_AMT FROM TB_SL_SALE_PAY_PARTNER tsspp , TB_MS_PARTNER tmp WHERE tsspp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsspp.SALE_DATE = #{saleDate}
AND tsspp.SALE_DATE BETWEEN #{yearMonth}||'01' AND #{yearMonth} ||'31'
AND tsspp.STORE_CD IN
#{item}
AND tmp.STORE_CD = tsspp.STORE_CD AND tmp.PARTN_CD = tsspp.PARTN_CD GROUP BY tsspp.PARTN_CD, tmp.PARTN_NM ORDER BY tsspp.PARTN_CD

