        SUM(SALE_AMT) AS SALE_AMT
FROM 	TB_SL_SALE_PAY_VORDER
WHERE   HQ_OFFICE_CD        =   #{hqOfficeCd}






                AND SALE_DATE = #{saleDate}





                AND SALE_DATE BETWEEN #{yearMonth}||'01' AND #{yearMonth} ||'31'





    AND STORE_CD IN

        #{item}


GROUP
BY      PICKUP_FG
ORDER
BY      PICKUP_FG
