mapper.select:
/* USE : DcInfoMapper.getGeneralDcList */
/* 현재 일반할인에 대한 정보 테이블이나 명칭이 없어 TB_SL_SALE_DTL_DC 테이블의 할인사유 보여주도록만 해둠. */

SELECT  tssdd.DC_REASON_NM
,       tssdd.DC_AMT
FROM    TB_SL_SALE_DTL_DC tssdd
WHERE   tssdd.HQ_OFFICE_CD        =   #{hqOfficeCd}
AND     tssdd.SALE_DATE           =   #{saleDate}


    AND tssdd.STORE_CD = #{storeCd}


    AND (
            'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
        OR  tssdd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
        )


    AND tssdd.POS_NO = #{posNo}


    AND tssdd.BILL_NO = #{billNo}


AND     tssdd.DC_CD = #{dcCd}
ORDER
BY      tssdd.DC_REASON_NM

/* USE : DcInfoMapper.getCoupnDcList */
SELECT tmc.COUPN_NM , tssd.PROD_CD, tmp.PROD_NM , tssd.SALE_UPRC , tssd.SALE_QTY , tssdd.DC_AMT FROM TB_SL_SALE_DTL_DC tssdd , TB_SL_SALE_DTL tssd , ( SELECT STORE_CD, PAY_CLASS_CD||COUPN_CD AS COUPN_CD , COUPN_NM FROM TB_MS_COUPON WHERE STORE_CD IN ( SELECT STORE_CD FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd} )
AND
STORE_CD = #{item}
) tmc , TB_MS_PRODUCT tmp WHERE tssdd.HQ_OFFICE_CD = #{hqOfficeCd} AND tssdd.SALE_DATE = #{saleDate}
AND
tssdd.STORE_CD = #{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tssdd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tssdd.POS_NO = #{posNo}
AND tssdd.BILL_NO = #{billNo}
AND tssdd.DC_CD = #{dcCd} AND tssd.HQ_OFFICE_CD (+) = tssdd.HQ_OFFICE_CD AND tssd.STORE_CD (+) = tssdd.STORE_CD AND tssd.SALE_DATE (+) = tssdd.SALE_DATE AND tssd.POS_NO (+) = tssdd.POS_NO AND tssd.BILL_NO (+) = tssdd.BILL_NO AND tssd.BILL_DTL_NO (+) = tssdd.BILL_DTL_NO AND tmc.STORE_CD (+) = tssdd.STORE_CD AND tmc.COUPN_CD (+) = tssdd.DC_REASON_CD AND tmp.STORE_CD = tssd.STORE_CD AND tmp.PROD_CD = tssd.PROD_CD ORDER BY tmc.COUPN_NM, tssd.PROD_CD

/* USE : DcInfoMapper.getMembrDcList */
SELECT tmm.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM , tmm.MEMBR_CLASS_CD, tmmc.MEMBR_CLASS_NM , tmmc.POINT_SAVE_FG, tcn.NMCODE_NM AS POINT_SAVE_FG_NM , tmmc.DC_RATE , tssdd.DC_AMT FROM TB_SL_SALE_DTL_DC tssdd , TB_SL_SALE_HDR_MEMBR tsshm , TB_MB_MEMBER tmm , TB_MB_MEMBER_CLASS tmmc , TB_CM_NMCODE tcn WHERE tssdd.HQ_OFFICE_CD = #{hqOfficeCd} AND tssdd.SALE_DATE = #{saleDate}
AND tssdd.STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tssdd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tssdd.POS_NO = #{posNo}
AND tssdd.BILL_NO = #{billNo}
AND tssdd.DC_CD = #{dcCd} AND tsshm.HQ_OFFICE_CD = tssdd.HQ_OFFICE_CD AND tsshm.STORE_CD = tssdd.STORE_CD AND tsshm.SALE_DATE = tssdd.SALE_DATE AND tsshm.POS_NO = tssdd.POS_NO AND tsshm.BILL_NO = tssdd.BILL_NO AND tmm.MEMBR_ORGN_CD = #{orgnGrpCd} AND tmm.MEMBR_NO = tsshm.MEMBR_NO AND tmmc.MEMBR_ORGN_CD = tmm.MEMBR_ORGN_CD AND tmmc.MEMBR_CLASS_CD = tmm.MEMBR_CLASS_CD AND tcn.NMCODE_GRP_CD = '054' AND tcn.NMCODE_CD = tmmc.POINT_SAVE_FG ORDER BY tmm.MEMBR_NO, tmm.MEMBR_NM

/* USE : DcInfoMapper.getPartnerDcList */
SELECT tsspp.PARTN_CD, tmp.PARTN_NM , tsspp.PARTN_CARD_NO , SUBSTR(tsspp.APPR_DT, 0, 8) AS APPR_DATE , SUBSTR(tsspp.APPR_DT, 9, 14) AS APPR_TIME , tsspp.APPR_NO , tsspp.DC_AMT FROM TB_SL_SALE_HDR_DC tsshd , TB_SL_SALE_PAY_PARTNER tsspp , TB_MS_PARTNER tmp /* 운영서버에는 테이블이 있지만 개발서버에는 현재 없어서 오류남. 주석 달아달라고하여 주석처리만 해둠. */ WHERE tsshd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsshd.SALE_DATE = #{saleDate}
AND tsshd.STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsshd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsshd.POS_NO = #{posNo}
AND tsshd.BILL_NO = #{billNo}
AND tsshd.DC_CD = #{dcCd} AND tsspp.HQ_OFFICE_CD = tsshd.HQ_OFFICE_CD AND tsspp.STORE_CD = tsshd.STORE_CD AND tsspp.SALE_DATE = tsshd.SALE_DATE AND tsspp.POS_NO = tsshd.POS_NO AND tsspp.BILL_NO = tsshd.BILL_NO AND tmp.STORE_CD = tsspp.STORE_CD AND tmp.PARTN_CD = tsspp.PARTN_CD ORDER BY tsspp.PARTN_CD, tmp.PARTN_NM

/* USE : DcInfoMapper.getServiceDcList */
/* 현재 서비스에 대한 정보 테이블이나 명칭이 없어 TB_SL_SALE_DTL_DC 테이블의 할인사유 보여주도록만 해둠. */

SELECT  tssdd.DC_REASON_NM
,       tssdd.DC_AMT
FROM    TB_SL_SALE_DTL_DC tssdd
WHERE   tssdd.HQ_OFFICE_CD        =   #{hqOfficeCd}
AND     tssdd.SALE_DATE           =   #{saleDate}


    AND tssdd.STORE_CD = #{storeCd}


    AND tssdd.POS_NO = #{posNo}


    AND (
            'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
        OR  tssdd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
        )


    AND tssdd.BILL_NO = #{billNo}


AND     tssdd.DC_CD = #{dcCd}
ORDER
BY      tssdd.DC_REASON_NM

/* USE : DcInfoMapper.getPromtnDcList */
SELECT tssd.PROMTN_NO, tcn.NMCODE_NM AS PROMTN_PROD_FG_NM , tssdd.PROD_CD, tmp.PROD_NM , tssdd.DC_AMT FROM TB_SL_SALE_DTL_DC tssdd , TB_SL_SALE_DTL tssd , TB_MS_PRODUCT tmp , TB_CM_NMCODE tcn WHERE tssdd.HQ_OFFICE_CD = #{hqOfficeCd} AND tssdd.SALE_DATE = #{saleDate}
AND tssdd.STORE_CD = #{storeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tssdd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tssdd.POS_NO = #{posNo}
AND tssdd.BILL_NO = #{billNo}
AND tssdd.DC_CD = #{dcCd} AND tssd.HQ_OFFICE_CD (+) = tssdd.HQ_OFFICE_CD AND tssd.STORE_CD (+) = tssdd.STORE_CD AND tssd.SALE_DATE (+) = tssdd.SALE_DATE AND tssd.POS_NO (+) = tssdd.POS_NO AND tssd.BILL_NO (+) = tssdd.BILL_NO AND tssd.BILL_DTL_NO (+) = tssdd.BILL_DTL_NO AND tmp.STORE_CD = tssdd.STORE_CD AND tmp.PROD_CD = tssdd.PROD_CD AND tcn.NMCODE_GRP_CD = '052' AND tcn.NMCODE_CD = tssd.PROMTN_PROD_FG ORDER BY tssd.PROMTN_NO, tcn.NMCODE_NM, tssdd.PROD_CD

/* USE : DcInfoMapper.getVcoupnDcList */
SELECT tsspv.VCOUPN_NO , tsspv.VCOUPN_NM , tsspv.VCOUPN_TYPE , tsspv.VCOUPN_APPR_NO , tsspv.VCOUPN_DC_AMT , tsspv.VCOUPN_SAVE_POINT , tsspv.MEMBR_ORDER_NO FROM TB_SL_SALE_HDR_DC tsshd , TB_SL_SALE_PAY_VCOUPN tsspv WHERE tsshd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsshd.SALE_DATE = #{saleDate}
AND
tsshd.STORE_CD = #{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsshd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsshd.POS_NO = #{posNo}
AND tsshd.BILL_NO = #{billNo}
AND tsshd.DC_CD = #{dcCd} AND tsspv.HQ_OFFICE_CD = tsshd.HQ_OFFICE_CD AND tsspv.STORE_CD = tsshd.STORE_CD AND tsspv.SALE_DATE = tsshd.SALE_DATE AND tsspv.POS_NO = tsshd.POS_NO AND tsspv.BILL_NO = tsshd.BILL_NO ORDER BY tsspv.VCOUPN_NO , tsspv.VCOUPN_NM

/* USE : DcInfoMapper.getSmartorderDcList */

SELECT  tsspv.PICKUP_FG,
        SUBSTR(tsspv.APPR_DT, 9, 14) AS APPR_TIME,
        tsspv.PICKUP_TEL_NO,
        tsspv.DC_AMT
FROM    TB_SL_SALE_HDR_DC tsshd
,       TB_SL_SALE_PAY_VORDER tsspv
WHERE   tsshd.HQ_OFFICE_CD        =   #{hqOfficeCd}
AND     tsshd.SALE_DATE           =   #{saleDate}


    AND

        tsshd.STORE_CD = #{item}



    AND (
            'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
        OR  tsshd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
        )


    AND tsshd.POS_NO = #{posNo}


    AND tsshd.BILL_NO = #{billNo}


AND     tsshd.DC_CD               =   #{dcCd}
AND     tsspv.HQ_OFFICE_CD        =   tsshd.HQ_OFFICE_CD
AND     tsspv.STORE_CD            =   tsshd.STORE_CD
AND     tsspv.SALE_DATE           =   tsshd.SALE_DATE
AND     tsspv.POS_NO              =   tsshd.POS_NO
AND     tsspv.BILL_NO             =   tsshd.BILL_NO
ORDER
BY      tsspv.PICKUP_FG, tsspv.APPR_DT
