AND tsspgd.STORE_CD = #{storeCd}
AND tsspgd.POS_NO = #{posNo}
AND tsspgd.BILL_NO = #{billNo}
AND tsspgr.HQ_OFFICE_CD (+) = tsspgd.HQ_OFFICE_CD AND tsspgr.STORE_CD (+) = tsspgd.STORE_CD AND tsspgr.SALE_DATE (+) = tsspgd.SALE_DATE AND tsspgr.POS_NO (+) = tsspgd.POS_NO AND tsspgr.BILL_NO (+) = tsspgd.BILL_NO AND tsspgr.LINE_NO (+) = tsspgd.LINE_NO AND tmg.STORE_CD (+) = tsspgd.STORE_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tmg.GIFT_CD (+) = tsspgd.GIFT_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */ AND tcn.NMCODE_GRP_CD = '048' AND tcn.NMCODE_CD = tsspgd.GIFT_PROC_FG ORDER BY tsspgd.GIFT_SEQ

/* USE : PayInfoMapper.getFstmpList */

SELECT 	tsspfd.FSTMP_SEQ
,       tsspfd.FSTMP_CD, tmfs.FSTMP_NM
,       tsspfd.FSTMP_UPRC
,       tsspfd.FSTMP_QTY
,       tsspfd.FSTMP_UPRC * tsspfd.FSTMP_QTY AS FSTMP_TOT_AMT
,       tsspfd.FSTMP_SER_NO
FROM 	TB_SL_SALE_PAY_FSTMP_DTL tsspfd
,       TB_MS_FOOD_STAMP tmfs
WHERE   tsspfd.HQ_OFFICE_CD        =   #{hqOfficeCd}
AND     tsspfd.STORE_CD            =   #{storeCd}
AND     tsspfd.SALE_DATE           =   #{saleDate}


    AND tsspfd.STORE_CD = #{storeCd}


    AND tsspfd.POS_NO = #{posNo}


    AND tsspfd.BILL_NO = #{billNo}


AND     tmfs.STORE_CD          (+) =   tsspfd.STORE_CD
AND     tmfs.FSTMP_CD          (+) =   tsspfd.FSTMP_CD
ORDER
BY      tsspfd.FSTMP_SEQ

