    AND tsspt.STORE_CD = #{storeCd}


    AND tsspt.POS_NO = #{posNo}


    AND tsspt.BILL_NO = #{billNo}


AND     tmc.STORE_CD          (+) =   tsspt.STORE_CD /* 개발엔 자료가 없어서 (+) 검. 나중엔 (+) 빼야함. */
