, tsshp.PAY02 AS PAY02 -- 현금 , tsshp.PAY021 AS PAY021 -- 현금영수증 FROM ( SELECT tssh.SALE_DATE , TO_CHAR(TO_DATE(tssh.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , COUNT(tssh.STORE_CD) AS STORE_CNT , SUM(tssh.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tssh.TOT_DC_AMT) AS TOT_DC_AMT , SUM(tssh.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(tssh.REAL_SALE_AMT) - SUM(tssh.VAT_AMT) AS GA_AMT , SUM(tssh.VAT_AMT) AS VAT_AMT , SUM(tssh.TOT_TIP_AMT) AS TOT_TIP_AMT , SUM(tssh.TOT_ETC_AMT) AS TOT_ETC_AMT , SUM(tssh.CUP_AMT) AS CUP_AMT , SUM(tssh.BILL_CNT) AS BILL_CNT , DECODE(SUM(tssh.BILL_CNT), 0, 0, ROUND(SUM(tssh.REAL_SALE_AMT) / SUM(tssh.BILL_CNT))) AS BILL_UPRC FROM ( SELECT tssh.SALE_DATE, tssh.STORE_CD , SUM(tssh.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tssh.TOT_DC_AMT) AS TOT_DC_AMT , SUM(tssh.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(tssh.REAL_SALE_AMT) - SUM(tssh.VAT_AMT) AS GA_AMT , SUM(tssh.VAT_AMT) AS VAT_AMT , SUM(tssh.TOT_TIP_AMT) AS TOT_TIP_AMT , SUM(tssh.TOT_ETC_AMT) AS TOT_ETC_AMT , SUM(tssh.CUP_AMT) AS CUP_AMT , COUNT(tssh.BILL_NO) AS BILL_CNT FROM TB_SL_SALE_HDR tssh WHERE tssh.HQ_OFFICE_CD = #{hqOfficeCd}
