AND tssd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tssd.SALE_DATE ) tssddp , ( SELECT tsshp.SALE_DATE, SUM(PAY_AMT) - NVL(SUM(CASE tsshd.DLVR_IN_FG WHEN '1' THEN PAY_AMT WHEN '3' THEN PAY_AMT WHEN '4' THEN PAY_AMT END), 0) AS PAY0, SUM(CASE tsshd.DLVR_IN_FG WHEN '1' THEN PAY_AMT END) AS PAY1, SUM(CASE tsshd.DLVR_IN_FG WHEN '3' THEN PAY_AMT END) AS PAY3, SUM(CASE tsshd.DLVR_IN_FG WHEN '4' THEN PAY_AMT END) AS PAY4 FROM TB_SL_SALE_HDR_PAY tsshp LEFT JOIN TB_SL_SALE_HDR_DLVR tsshd ON tsshd.STORE_CD = tsshp.STORE_CD AND tsshd.SALE_DATE = tsshp.SALE_DATE AND tsshd.POS_NO = tsshp.POS_NO AND tsshd.BILL_NO = tsshp.BILL_NO WHERE tsshp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsshp.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsshp.PAY_CD = '02'
