/* USE : DayMapper.getDayStoreDcList */ SELECT tsshd.HQ_OFFICE_CD, tsshd.STORE_CD, (SELECT STORE_NM FROM TB_MS_STORE WHERE HQ_OFFICE_CD = tsshd.HQ_OFFICE_CD AND STORE_CD = tsshd.STORE_CD) AS STORE_NM,
NVL(tsshd.DC${item}, 0) AS TOT_DC_AMT,
NVL(tsshd.DC${item}, 0) AS DC${item}
FROM ( SELECT HQ_OFFICE_CD, STORE_CD,
SUM(DC${item}) AS DC${item}
FROM ( SELECT HQ_OFFICE_CD, STORE_CD, DC_CD, DC_AMT FROM TB_SL_SALE_HDR_DC WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND SALE_DATE = #{saleDate}
AND SALE_DATE BETWEEN #{yearMonth}||'01' AND #{yearMonth} ||'31'
AND STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
) PIVOT ( SUM(DC_AMT) FOR DC_CD IN (${pivotDcCol}) ) GROUP BY HQ_OFFICE_CD, STORE_CD ) tsshd WHERE 1=1 ORDER BY HQ_OFFICE_CD, STORE_CD

/* USE : DayMapper.getDayDcList */

SELECT  TO_CHAR(TO_DATE(tsshd.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE
,       TO_CHAR(TO_DATE(tsshd.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL
,       tsshd.STORE_CNT

,       
            NVL(tsshd.DC${item}, 0)
         AS TOT_DC_AMT
,       
            tsshd.DC${item} AS DC${item}


FROM    (   SELECT  SALE_DATE
            ,       COUNT(STORE_CD) AS STORE_CNT

            ,       
                        SUM(DC${item}) AS DC${item}


            FROM    (   SELECT  tsshd.SALE_DATE, tsshd.STORE_CD, tsshd.DC_CD, tsshd.DC_AMT
                        FROM    TB_SL_SALE_HDR_DC tsshd
                        WHERE   tsshd.HQ_OFFICE_CD  = #{hqOfficeCd}
                        AND     tsshd.SALE_DATE BETWEEN #{startDate} AND #{endDate}


                            AND tsshd.STORE_CD IN

                                #{item}



                            AND (
                                'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001')
                            OR  tsshd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo})
                            )


                    )
            PIVOT   (
                        SUM(DC_AMT)
                        FOR DC_CD
                        IN (${pivotDcCol})
                    )
            GROUP BY SALE_DATE
        ) tsshd
ORDER
BY      tsshd.SALE_DATE DESC

/* USE : DayMapper.getDayTaxList */
SELECT TO_CHAR(TO_DATE(tssh.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tssh.YOIL , tssh.STORE_CNT , tssh.TOT_SALE_AMT , tssh.TOT_DC_AMT , tssh.REAL_SALE_AMT , tssh.TAX_SALE_AMT , tssh.GA_AMT , tssh.VAT_AMT , tssh.NO_TAX_SALE_AMT , tssh.NET_SALE_AMT FROM ( SELECT tssh.SALE_DATE , TO_CHAR(TO_DATE(tssh.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , COUNT(tssh.STORE_CD) AS STORE_CNT , SUM(tssh.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tssh.TOT_DC_AMT) AS TOT_DC_AMT , SUM(tssh.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(tssh.TAX_SALE_AMT) AS TAX_SALE_AMT , SUM(tssh.TAX_SALE_AMT) - SUM(tssh.VAT_AMT) AS GA_AMT , SUM(tssh.VAT_AMT) AS VAT_AMT , SUM(tssh.NO_TAX_SALE_AMT) AS NO_TAX_SALE_AMT , SUM(tssh.NET_SALE_AMT) AS NET_SALE_AMT FROM ( SELECT tssh.SALE_DATE, tssh.STORE_CD , SUM(tssh.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tssh.TOT_DC_AMT) AS TOT_DC_AMT , SUM(tssh.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(tssh.TAX_SALE_AMT) AS TAX_SALE_AMT , SUM(tssh.TAX_SALE_AMT) - SUM(tssh.VAT_AMT) AS GA_AMT , SUM(tssh.VAT_AMT) AS VAT_AMT , SUM(tssh.NO_TAX_SALE_AMT) AS NO_TAX_SALE_AMT , SUM(tssh.NET_SALE_AMT) AS NET_SALE_AMT FROM TB_SL_SALE_HDR tssh WHERE tssh.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssh.STORE_CD IN
