SELECT TO_CHAR(TO_DATE(tssh.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tssh.YOIL , COUNT(tssh.STORE_cd) AS STORE_CNT , SUM(tssh.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(tssh.SALE_CNT) AS SALE_CNT , SUM(tssh.TOT_GUEST_CNT) AS TOT_GUEST_CNT ${sQuery1} FROM ( SELECT SALE_DATE , TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , STORE_CD , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(SALE_CNT) AS SALE_CNT , SUM(GUEST_CNT) AS TOT_GUEST_CNT ${sQuery2} FROM ( SELECT SALE_DATE , TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , STORE_CD , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(SALE_CNT) AS SALE_CNT , SUM(GUEST_CNT_1) AS GUEST_CNT , thn.TIME_SLOT FROM TB_SL_DAILY_TIME tsdt, (SELECT MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) || LPAD(MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) + 1, 2, '0') AS TIME_SLOT, MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) AS START_TIME, MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) AS END_TIME
FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
FROM TB_MS_STORE_NMCODE WHERE STORE_CD = #{storeCd}
AND NMCODE_GRP_CD ='001' GROUP BY NMCODE_NM ) thn WHERE tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdt.SALE_HOUR BETWEEN thn.START_TIME AND thn.END_TIME
AND STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND TIME_SLOT = replace(#{timeSlot},'~','')
GROUP BY SALE_DATE, STORE_CD, TIME_SLOT ) GROUP BY SALE_DATE, STORE_CD ) tssh GROUP BY SALE_DATE, YOIL ORDER BY SALE_DATE DESC

