mapper.select:
/* DayPeriodMapper.getDayPeriodTimeList */
SELECT SALE_HOUR AS SALE_TIME, SUM(REAL_SALE_CNT) AS BILL_CNT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, DECODE(SUM(REAL_SALE_CNT), 0, 0, ROUND(SUM(REAL_SALE_AMT) / SUM(REAL_SALE_CNT))) AS BILL_UPRC FROM TB_SL_DAILY_TIME WHERE HQ_OFFICE_CD = #{membrOrgnCd}
AND STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND SALE_HOUR BETWEEN #{startTime} AND #{endTime}
AND SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY SALE_HOUR ORDER BY SALE_HOUR
SELECT TIME_SLOT AS SALE_TIME, SUM(REAL_SALE_CNT) AS BILL_CNT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, DECODE(SUM(REAL_SALE_CNT), 0, 0, ROUND(SUM(REAL_SALE_AMT) / SUM(REAL_SALE_CNT))) AS BILL_UPRC FROM TB_SL_DAILY_TIME, (SELECT MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) || '~' || LPAD(MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) + 1, 2, '0') AS TIME_SLOT, MIN(NMCODE_CD) KEEP(DENSE_RANK FIRST ORDER BY NMCODE_ITEM_1) AS START_TIME, MAX(NMCODE_CD) KEEP(DENSE_RANK LAST ORDER BY NMCODE_ITEM_1) AS END_TIME
FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
FROM TB_MS_STORE_NMCODE WHERE STORE_CD = #{storeCd}
AND NMCODE_GRP_CD ='001' GROUP BY NMCODE_NM ) thn WHERE HQ_OFFICE_CD = #{membrOrgnCd}
AND STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND TIME_SLOT = #{timeSlot}
AND SALE_DATE BETWEEN #{startDate} AND #{endDate} AND SALE_HOUR BETWEEN START_TIME AND END_TIME GROUP BY TIME_SLOT ORDER BY TIME_SLOT

/* DayPeriodMapper.getDayPeriodTimeDetailList */ SELECT tsdpt.PROD_CD, tmp.PROD_NM, SUM(tsdpt.TOT_SALE_QTY) AS SALE_QTY, SUM(tsdpt.TOT_SALE_AMT - tsdpt.TOT_DC_AMT - tsdpt.TOT_TIP_AMT - tsdpt.TOT_ETC_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_PROD_TIME tsdpt, TB_MS_PRODUCT tmp WHERE tsdpt.HQ_OFFICE_CD = #{membrOrgnCd}
AND tsdpt.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdpt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdpt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdpt.SALE_HOUR = ${saleTime}
AND tsdpt.SALE_HOUR BETWEEN SUBSTR(#{saleTime}, 0, 2) AND SUBSTR(#{saleTime}, -2) -1
AND tmp.STORE_CD (+)= tsdpt.STORE_CD AND tmp.PROD_CD (+)= tsdpt.PROD_CD GROUP BY tsdpt.PROD_CD, tmp.PROD_NM ORDER BY tsdpt.PROD_CD

/* DayPeriodMapper.getDayPeriodProdClassList */ SELECT tssd1.Level1 AS PROD_CLASS_CD, tssd1.Level1_NM AS PROD_CLASS_NM, tssd1.SALE_QTY, tssd1.REAL_SALE_AMT, ROUND(tssd1.REAL_SALE_AMT / DECODE(tssd2.TOT_REAL_SALE_AMT, 0, null, tssd2.TOT_REAL_SALE_AMT) * 100, 2) AS REAL_SALE_AMT_PER FROM ( SELECT REPLACE(SUBSTR(PATH, 0, 6) ,'▶','') AS Level1, FN_GET_PROD_CLASS_CD_NM_STR(STORE_CD, REPLACE(SUBSTR(PATH, 0, 6) ,'▶',''), 'NM') AS Level1_NM, SUM(SALE_QTY) AS SALE_QTY, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT tmp.STORE_CD, FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'CD_STR') AS PATH, SUM(tssd.SALE_QTY) AS SALE_QTY, SUM(tssd.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_SALE_DTL tssd, TB_MS_PRODUCT tmp WHERE 1=1 AND tssd.HQ_OFFICE_CD = #{membrOrgnCd}
AND tssd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tssd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tssd.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tmp.STORE_CD = tssd.STORE_CD AND tmp.PROD_CD = tssd.PROD_CD GROUP BY tmp.STORE_CD, tmp.PROD_CLASS_CD ) GROUP BY REPLACE(SUBSTR(PATH, 0, 6) ,'▶',''), FN_GET_PROD_CLASS_CD_NM_STR(STORE_CD, REPLACE(SUBSTR(PATH, 0, 6) ,'▶',''), 'NM') ) tssd1, ( SELECT SUM(tssd.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT FROM TB_SL_SALE_DTL tssd WHERE 1=1 AND tssd.HQ_OFFICE_CD = #{membrOrgnCd}
AND tssd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tssd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tssd.SALE_DATE BETWEEN #{startDate} AND #{endDate} ) tssd2 ORDER BY tssd1.Level1

/* DayPeriodMapper.getDayPeriodProdClassDetailList */ SELECT tmp.PROD_CLASS_CD, tsdp.PROD_CD, tmp.PROD_NM, SUM(tsdp.TOT_SALE_QTY) AS SALE_QTY, SUM(tsdp.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_PROD tsdp, TB_MS_PRODUCT tmp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{membrOrgnCd}
AND tsdp.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdp.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tmp.STORE_CD = tsdp.STORE_CD AND tmp.PROD_CD = tsdp.PROD_CD AND tmp.PROD_CLASS_CD IN ( SELECT #{prodClassCd} AS PROD_CLASS_CD FROM DUAL UNION ALL SELECT tmpc.PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS tmpc WHERE tmpc.STORE_CD = tmp.STORE_CD START WITH tmpc.P_PROD_CLASS_CD = #{prodClassCd} AND tmpc.STORE_CD = tmp.STORE_CD CONNECT BY tmpc.P_PROD_CLASS_CD = PRIOR tmpc.PROD_CLASS_CD AND tmpc.STORE_CD = tmp.STORE_CD ) GROUP BY tmp.PROD_CLASS_CD, tsdp.PROD_CD, tmp.PROD_NM ORDER BY tsdp.PROD_CD

/* DayPeriodMapper.getDayPeriodTableList */ SELECT TBL_CD, TBL_NM, COUNT(TBL_CNT) AS TBL_CNT, SUM(TOT_GUEST_CNT) AS TOT_GUEST_CNT, SUM(TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(TOT_DC_AMT) AS TOT_DC_AMT, SUM(TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM ( SELECT tssh.TBL_CD, tmt.TBL_NM, --속성 --평균점유시간 1 AS TBL_CNT, tssh.TOT_GUEST_CNT, tssh.TOT_SALE_AMT, tssh.TOT_DC_AMT, tssh.TOT_TIP_AMT, tssh.REAL_SALE_AMT FROM TB_SL_SALE_HDR tssh, TB_MS_TABLE_V01 tmt WHERE 1=1 AND tssh.HQ_OFFICE_CD = #{membrOrgnCd}
AND tssh.STORE_CD = #{storeCd}
AND tssh.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tssh.TBL_CD != '000' AND tmt.STORE_CD (+)= tssh.STORE_CD AND tmt.TBL_CD (+)= tssh.TBL_CD ) GROUP BY TBL_CD, TBL_NM ORDER BY TBL_CD

/* DayPeriodMapper.getDayPeriodCornerList */ SELECT tsdcp.STORE_CD, tsdcp.CORNR_CD, ('[' || tms.STORE_CD || ']' ||tms.STORE_NM || '-[' || tmc.CORNR_CD || ']' || tmc.CORNR_NM) AS STORE_CORNR_NM, SUM(tsdcp.TOT_SALE_QTY) AS SALE_QTY, SUM(tsdcp.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_CORNR tsdcp, TB_MS_STORE tms, TB_MS_CORNER tmc WHERE 1=1 AND tsdcp.HQ_OFFICE_CD = #{membrOrgnCd}
AND tsdcp.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdcp.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdcp.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tms.HQ_OFFICE_CD (+)= tsdcp.HQ_OFFICE_CD AND tms.STORE_CD (+)= tsdcp.STORE_CD AND tmc.STORE_CD (+)= tsdcp.STORE_CD AND tmc.CORNR_CD (+)= tsdcp.CORNR_CD GROUP BY tsdcp.STORE_CD, tsdcp.CORNR_CD, tms.STORE_CD, tms.STORE_NM, tmc.CORNR_NM, tmc.CORNR_CD ORDER BY tsdcp.STORE_CD, tsdcp.CORNR_CD

/* DayPeriodMapper.getDayPeriodGiftList */ SELECT tsspgd.STORE_CD, tmg.PAY_CLASS_CD, tmpmc.PAY_CLASS_NM, SUM(CASE WHEN tsspgd.GIFT_PROC_FG = '1' THEN tsspgd.GIFT_QTY ELSE 0 END) AS GIFT_QTY_USE, SUM(CASE WHEN tsspgd.GIFT_PROC_FG = '1' THEN tsspgd.GIFT_UPRC ELSE 0 END) AS GIFT_UPRC_USE, SUM(CASE WHEN tsspgd.GIFT_PROC_FG = '2' THEN tsspgd.GIFT_QTY ELSE 0 END) AS GIFT_QTY_REF, SUM(CASE WHEN tsspgd.GIFT_PROC_FG = '2' THEN tsspgd.GIFT_UPRC ELSE 0 END) AS GIFT_UPRC_REF FROM TB_SL_SALE_PAY_GIFT_DTL tsspgd, TB_MS_GIFT tmg, TB_MS_PAY_METHOD_CLASS tmpmc WHERE 1=1 AND tsspgd.HQ_OFFICE_CD = #{membrOrgnCd}
AND tsspgd.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsspgd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsspgd.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tmg.STORE_CD (+)= tsspgd.STORE_CD AND tmg.GIFT_CD (+)= tsspgd.GIFT_CD AND tmpmc.STORE_CD (+)= tmg.STORE_CD AND tmpmc.PAY_CLASS_CD (+)= tmg.PAY_CLASS_CD GROUP BY tsspgd.STORE_CD, tmg.PAY_CLASS_CD, tmpmc.PAY_CLASS_NM, tsspgd.GIFT_PROC_FG ORDER BY tsspgd.STORE_CD, tmg.PAY_CLASS_CD
