/* DayMomsMapper.getDayMomsList */ SELECT TO_CHAR(TO_DATE(tssh.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tssh.YOIL , tssh.STORE_CNT , tssh.TOT_SALE_AMT , tssh.TOT_DC_AMT , tssh.REAL_SALE_AMT , tssh.GA_AMT , tssh.VAT_AMT , tssh.TOT_TIP_AMT , tssh.TOT_ETC_AMT , tssh.CUP_AMT , tssh.BILL_CNT , tssh.BILL_UPRC , tssddp.GEN_REAL_SALE_AMT , DECODE(tssh.REAL_SALE_AMT, 0, 0, (tssddp.GEN_REAL_SALE_AMT / tssh.REAL_SALE_AMT)*100) AS GEN_REAL_SALE_RATE , tssddp.DLVR_REAL_SALE_AMT , DECODE(tssh.REAL_SALE_AMT, 0, 0, (tssddp.DLVR_REAL_SALE_AMT / tssh.REAL_SALE_AMT)*100) AS DLVR_REAL_SALE_RATE , tssddp.PACK_REAL_SALE_AMT , DECODE(tssh.REAL_SALE_AMT, 0, 0, (tssddp.PACK_REAL_SALE_AMT / tssh.REAL_SALE_AMT)*100) AS PACK_REAL_SALE_RATE , NVL(tsshp.PAY${item}, 0) + NVL(tsshp.PAY02, 0) + NVL(tsshp.PAY021, 0) AS TOT_PAY_AMT , tsshp.PAY${item} AS PAY${item}
, tsshp.PAY02 AS PAY02 -- 현금 , tsshp.PAY021 AS PAY021 -- 현금영수증 , tsdto.SALE_QTY${item} AS SALE_QTY${item} , tsdto.REAL_SALE_AMT${item} AS REAL_SALE_AMT${item}
FROM ( SELECT tssh.SALE_DATE , TO_CHAR(TO_DATE(tssh.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , COUNT(tssh.STORE_CD) AS STORE_CNT , SUM(tssh.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tssh.TOT_DC_AMT) AS TOT_DC_AMT , SUM(tssh.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(tssh.REAL_SALE_AMT) - SUM(tssh.VAT_AMT) AS GA_AMT , SUM(tssh.VAT_AMT) AS VAT_AMT , SUM(tssh.TOT_TIP_AMT) AS TOT_TIP_AMT , SUM(tssh.TOT_ETC_AMT) AS TOT_ETC_AMT , SUM(tssh.CUP_AMT) AS CUP_AMT , SUM(tssh.BILL_CNT) AS BILL_CNT , DECODE(SUM(tssh.BILL_CNT), 0, 0, ROUND(SUM(tssh.REAL_SALE_AMT) / SUM(tssh.BILL_CNT))) AS BILL_UPRC FROM ( SELECT tssh.SALE_DATE, tssh.STORE_CD , SUM(tssh.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tssh.TOT_DC_AMT) AS TOT_DC_AMT , SUM(tssh.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(tssh.REAL_SALE_AMT) - SUM(tssh.VAT_AMT) AS GA_AMT , SUM(tssh.VAT_AMT) AS VAT_AMT , SUM(tssh.TOT_TIP_AMT) AS TOT_TIP_AMT , SUM(tssh.TOT_ETC_AMT) AS TOT_ETC_AMT , SUM(tssh.CUP_AMT) AS CUP_AMT , COUNT(tssh.BILL_NO) AS BILL_CNT FROM TB_SL_SALE_HDR tssh WHERE tssh.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssh.STORE_CD IN
#{item}
AND tssh.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tssh.SALE_DATE, tssh.STORE_CD ) tssh GROUP BY tssh.SALE_DATE ) tssh , ( SELECT SALE_DATE , SUM(PAY_AMT_${item}) AS PAY${item}
, SUM(NON_CASH_AMT) AS PAY02 , SUM(APPR_CASH_AMT) AS PAY021 FROM TB_SL_DAILY_TOTAL tsdt WHERE tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN
#{item}
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY SALE_DATE ) tsshp , ( SELECT tssd.SALE_DATE , SUM(CASE WHEN tssd.DLVR_PACK_FG = '1' THEN tssd.REAL_SALE_AMT ELSE 0 END) AS GEN_REAL_SALE_AMT , SUM(CASE WHEN tssd.DLVR_PACK_FG = '2' THEN tssd.REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT , SUM(CASE WHEN tssd.DLVR_PACK_FG = '3' THEN tssd.REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT FROM TB_SL_DAILY_DTL_DLVR tssd WHERE tssd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssd.STORE_CD IN
#{item}
AND tssd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tssd.SALE_DATE ) tssddp , ( SELECT SALE_DATE , SUM(CASE WHEN DLVR_IN_FG = ${item} THEN SALE_QTY END) AS SALE_QTY${item} , SUM(CASE WHEN DLVR_IN_FG = ${item} THEN REAL_SALE_AMT END) AS REAL_SALE_AMT${item}
FROM TB_SL_DAILY_TOTAL_ORDAPP tsdto WHERE tsdto.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdto.STORE_CD IN
#{item}
AND tsdto.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY SALE_DATE ) tsdto WHERE tsshp.SALE_DATE (+) = tssh.SALE_DATE AND tssddp.SALE_DATE (+) = tssh.SALE_DATE AND tsdto.SALE_DATE (+) = tssh.SALE_DATE ORDER BY tssh.SALE_DATE DESC

/* DayMomsMapper.getDayMomsExcelList */ SELECT TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsdt.STORE_CD , tms.STORE_NM , tms.AREA_CD -- 지역2 -- 상권 -- 상권2 , tsdt.BILL_CNT , DECODE(tsdt.BILL_CNT, 0, 0, ROUND(tsdt.TOT_SALE_AMT/tsdt.BILL_CNT)) AS BILL_UPRC , tsdt.TOT_GUEST_CNT , DECODE(tsdt.TOT_GUEST_CNT, 0, 0, ROUND(tsdt.TOT_SALE_AMT/tsdt.TOT_GUEST_CNT)) AS GUEST_UPRC , ( SELECT COUNT(*) FROM TB_SL_SALE_DTL tssd WHERE tssd.HQ_OFFICE_CD = tsdt.HQ_OFFICE_CD AND tssd.STORE_CD = tsdt.STORE_CD AND tssd.SALE_DATE = tsdt.SALE_DATE ) AS SALE_QTY , tsdt.TOT_SALE_AMT , tsdt.REAL_SALE_AMT , tsdt.TOT_DC_AMT , tsdt.PAY_AMT_02 AS CASH_ATM , tsdt.PAY_AMT_01 AS CARD_AMT , (tsdt.PAY_AMT_03 + tsdt.PAY_AMT_04 + tsdt.PAY_AMT_05 + tsdt.PAY_AMT_06 + tsdt.PAY_AMT_07 + tsdt.PAY_AMT_08 + tsdt.PAY_AMT_09 + tsdt.PAY_AMT_10 + tsdt.PAY_AMT_11 + tsdt.PAY_AMT_12 + tsdt.PAY_AMT_13 + tsdt.PAY_AMT_14 + tsdt.PAY_AMT_15 + tsdt.PAY_AMT_16 + tsdt.PAY_AMT_17 + tsdt.PAY_AMT_18 + tsdt.PAY_AMT_19 + tsdt.PAY_AMT_20) AS ETC_AMT FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms WHERE tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdt.STORE_CD IN
#{item}
ORDER BY tsdt.SALE_DATE, tsdt.STORE_CD
