mapper.select:
/* DayMomsMapper.getDayMomsList */ SELECT TO_CHAR(TO_DATE(tsshp.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tsshp.YOIL , tsshp.STORE_CNT , tsshp.TOT_SALE_AMT , tsshp.TOT_DC_AMT , tsshp.REAL_SALE_AMT , tsshp.GA_AMT , tsshp.VAT_AMT , tsshp.TOT_TIP_AMT , tsshp.TOT_ETC_AMT , tsshp.CUP_AMT , tsshp.BILL_CNT , tsshp.BILL_UPRC , tsshp.GEN_REAL_SALE_AMT , DECODE(tsshp.REAL_SALE_AMT, 0, 0, (tsshp.GEN_REAL_SALE_AMT / tsshp.REAL_SALE_AMT)*100) AS GEN_REAL_SALE_RATE , tsshp.DLVR_REAL_SALE_AMT , DECODE(tsshp.REAL_SALE_AMT, 0, 0, (tsshp.DLVR_REAL_SALE_AMT / tsshp.REAL_SALE_AMT)*100) AS DLVR_REAL_SALE_RATE , tsshp.PACK_REAL_SALE_AMT , DECODE(tsshp.REAL_SALE_AMT, 0, 0, (tsshp.PACK_REAL_SALE_AMT / tsshp.REAL_SALE_AMT)*100) AS PACK_REAL_SALE_RATE , NVL(tsshp.PAY${item}, 0) + NVL(tsshp.PAY02, 0) + NVL(tsshp.PAY021, 0) AS TOT_PAY_AMT , tsshp.PAY${item} AS PAY${item}
, tsshp.PAY02 AS PAY02 -- 현금 , tsshp.PAY021 AS PAY021 -- 현금영수증 , tsdto.SALE_QTY${item} AS SALE_QTY${item} , tsdto.REAL_SALE_AMT${item} AS REAL_SALE_AMT${item}
FROM ( SELECT SALE_DATE , TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , COUNT(DISTINCT STORE_CD) AS STORE_CNT , SUM(PAY_AMT_${item}) AS PAY${item}
, SUM(NON_CASH_AMT) AS PAY02 , SUM(APPR_CASH_AMT) AS PAY021 , SUM(TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TOT_DC_AMT) AS TOT_DC_AMT , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(REAL_SALE_AMT) - SUM( VAT_AMT) AS GA_AMT , SUM(VAT_AMT) AS VAT_AMT , SUM(TOT_TIP_AMT) AS TOT_TIP_AMT , SUM(TOT_ETC_AMT) AS TOT_ETC_AMT , SUM(CUP_AMT) AS CUP_AMT , SUM(REAL_SALE_CNT) AS BILL_CNT , DECODE(SUM(REAL_SALE_CNT), 0, 0, ROUND(SUM(REAL_SALE_AMT) / SUM(REAL_SALE_CNT))) AS BILL_UPRC , SUM(CASE WHEN DLVR_ORDER_FG = '1' THEN REAL_SALE_AMT ELSE 0 END) AS GEN_REAL_SALE_AMT , SUM(CASE WHEN DLVR_ORDER_FG = '2' THEN REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT , SUM(CASE WHEN DLVR_ORDER_FG = '3' THEN REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL_DLVR tsdt WHERE tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN
#{item}
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY SALE_DATE ) tsshp , ( SELECT SALE_DATE , SUM(CASE WHEN DLVR_IN_FG = ${item} THEN SALE_QTY END) AS SALE_QTY${item} , SUM(CASE WHEN DLVR_IN_FG = ${item} THEN REAL_SALE_AMT END) AS REAL_SALE_AMT${item}
FROM TB_SL_DAILY_TOTAL_ORDAPP tsdto WHERE tsdto.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdto.STORE_CD IN
#{item}
AND tsdto.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY SALE_DATE ) tsdto WHERE tsdto.SALE_DATE (+) = tsshp.SALE_DATE ORDER BY tsshp.SALE_DATE DESC
