mapper.select:
/* DayStoreMapper.getDayStoreList */
SELECT TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tsdt.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BEANCH_NM , SUM(tsdt.BILL_CNT) AS BILL_CNT , DECODE(SUM(tsdt.BILL_CNT), 0, 0, ROUND(SUM(tsdt.TOT_SALE_AMT)/SUM(tsdt.BILL_CNT))) AS BILL_UPRC , SUM(tsdt.TOT_GUEST_CNT) AS TOT_GUEST_CNT , DECODE(SUM(tsdt.TOT_GUEST_CNT), 0, 0, ROUND(SUM(tsdt.TOT_SALE_AMT)/SUM(tsdt.TOT_GUEST_CNT))) AS GUEST_UPRC , SUM(tsdt.SALE_CNT) AS SALE_CNT , SUM(tsdt.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tsdt.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(tsdt.TOT_DC_AMT) AS TOT_DC_AMT , SUM(tsdt.PAY_AMT_02) AS CASH_ATM , SUM(tsdt.PAY_AMT_01) AS CARD_AMT , SUM(tsdt.PAY_AMT_03 + tsdt.PAY_AMT_04 + tsdt.PAY_AMT_05 + tsdt.PAY_AMT_06 + tsdt.PAY_AMT_07 + tsdt.PAY_AMT_08 + tsdt.PAY_AMT_09 + tsdt.PAY_AMT_10 + tsdt.PAY_AMT_11 + tsdt.PAY_AMT_12 + tsdt.PAY_AMT_13 + tsdt.PAY_AMT_14 + tsdt.PAY_AMT_15 + tsdt.PAY_AMT_16 + tsdt.PAY_AMT_17 + tsdt.PAY_AMT_18 + tsdt.PAY_AMT_19 + tsdt.PAY_AMT_20) AS ETC_AMT
FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms WHERE tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND
tsdt.STORE_CD = #{item}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY tsdt.SALE_DATE, tsdt.HQ_OFFICE_CD, tms.BRANCH_CD

/* DayStoreMapper.getDayStoreExcelList */ SELECT TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tsdt.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BEANCH_NM , SUM(tsdt.BILL_CNT) AS BILL_CNT , DECODE(SUM(tsdt.BILL_CNT), 0, 0, ROUND(SUM(tsdt.TOT_SALE_AMT)/SUM(tsdt.BILL_CNT))) AS BILL_UPRC , SUM(tsdt.TOT_GUEST_CNT) AS TOT_GUEST_CNT , DECODE(SUM(tsdt.TOT_GUEST_CNT), 0, 0, ROUND(SUM(tsdt.TOT_SALE_AMT)/SUM(tsdt.TOT_GUEST_CNT))) AS GUEST_UPRC , SUM(tsdt.SALE_CNT) AS SALE_CNT , SUM(tsdt.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tsdt.REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(tsdt.TOT_DC_AMT) AS TOT_DC_AMT , SUM(tsdt.PAY_AMT_02) AS CASH_ATM , SUM(tsdt.PAY_AMT_01) AS CARD_AMT , SUM(tsdt.PAY_AMT_03 + tsdt.PAY_AMT_04 + tsdt.PAY_AMT_05 + tsdt.PAY_AMT_06 + tsdt.PAY_AMT_07 + tsdt.PAY_AMT_08 + tsdt.PAY_AMT_09 + tsdt.PAY_AMT_10 + tsdt.PAY_AMT_11 + tsdt.PAY_AMT_12 + tsdt.PAY_AMT_13 + tsdt.PAY_AMT_14 + tsdt.PAY_AMT_15 + tsdt.PAY_AMT_16 + tsdt.PAY_AMT_17 + tsdt.PAY_AMT_18 + tsdt.PAY_AMT_19 + tsdt.PAY_AMT_20) AS ETC_AMT FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms WHERE tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND
tsdt.STORE_CD = #{item}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY tsdt.SALE_DATE, tsdt.HQ_OFFICE_CD, tms.BRANCH_CD
