mapper.select:
/* DlvrFgMapper.getDlvrFg */



        SELECT COUNT(*)
          FROM TB_HQ_NMCODE
         WHERE HQ_OFFICE_CD = #{hqOfficeCd}
           AND NMCODE_GRP_CD ='113'



        SELECT COUNT(*)
          FROM TB_MS_STORE_NMCODE
         WHERE STORE_CD = #{storeCd}
           AND NMCODE_GRP_CD ='113'

/* DlvrFgMapper.getDlvrFgData */



        SELECT NMCODE_CD AS VALUE, NMCODE_NM AS NAME
          FROM TB_HQ_NMCODE
         WHERE HQ_OFFICE_CD = #{hqOfficeCd}
           AND NMCODE_GRP_CD ='113'



        SELECT NMCODE_CD AS VALUE, NMCODE_NM AS NAME
          FROM TB_MS_STORE_NMCODE
         WHERE STORE_CD = #{storeCd}
          AND NMCODE_GRP_CD ='113'

/* DlvrFgMapper.getOrderFg */
SELECT tsdpd.DLVR_PACK_FG, SUM(tsdpd.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdpd.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdpd.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(tsdpd.REAL_SALE_AMT) - SUM(tsdpd.TOT_VAT_AMT) AS TOT_GA_AMT, SUM(tsdpd.TOT_VAT_AMT) AS TOT_VAT_AMT, SUM(tsdpd.TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(tsdpd.TOT_ETC_AMT) AS TOT_ETC_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS TOT_PAY_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS PAY${item}
FROM TB_SL_DAILY_PROD_DLVR tsdpd, TB_MS_PRODUCT tmp WHERE tsdpd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdpd.STORE_CD = tmp.STORE_CD AND tsdpd.PROD_CD = tmp.PROD_CD AND tsdpd.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdpd.STORE_CD IN
#{item}
AND tsdpd.DLVR_PACK_FG = #{dlvrPackFg}
AND tsdpd.DLVR_IN_FG = #{dlvrInFg}
AND tmp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmp.PROD_CLASS_CD IN (SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS tmpc WHERE tmpc.STORE_CD = tsdpd.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND tmpc.STORE_CD = tsdpd.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND tmpc.STORE_CD = tsdpd.STORE_CD )
GROUP BY tsdpd.DLVR_PACK_FG ORDER BY tsdpd.DLVR_PACK_FG
SELECT tsdpd.DLVR_PACK_FG, SUM(tsdpd.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdpd.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdpd.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(tsdpd.REAL_SALE_AMT) - SUM(tsdpd.TOT_VAT_AMT) AS TOT_GA_AMT, SUM(tsdpd.TOT_VAT_AMT) AS TOT_VAT_AMT, SUM(tsdpd.TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(tsdpd.TOT_ETC_AMT) AS TOT_ETC_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS TOT_PAY_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS PAY${item}
FROM TB_SL_DAILY_PROD_DLVR tsdpd, TB_MS_PRODUCT tmp WHERE tsdpd.STORE_CD = #{storeCd} AND tsdpd.STORE_CD = tmp.STORE_CD AND tsdpd.PROD_CD = tmp.PROD_CD AND tsdpd.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdpd.DLVR_PACK_FG = #{dlvrPackFg}
AND tsdpd.DLVR_IN_FG = #{dlvrInFg}
AND tmp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmp.PROD_CLASS_CD IN (SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = #{storeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = #{storeCd} )
GROUP BY tsdpd.DLVR_PACK_FG ORDER BY tsdpd.DLVR_PACK_FG

/* DlvrFgMapper.getProd */
SELECT tsdpd.DLVR_PACK_FG, tsdpd.PROD_CD, tmp.PROD_NM, NVL(tmpb.BARCD_CD, '') AS BAR_CD, SUM(tsdpd.TOT_SALE_QTY) AS SALE_QTY, SUM(tsdpd.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdpd.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdpd.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(tsdpd.REAL_SALE_AMT) - SUM(tsdpd.TOT_VAT_AMT) AS TOT_GA_AMT, SUM(tsdpd.TOT_VAT_AMT) AS TOT_VAT_AMT, SUM(tsdpd.TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(tsdpd.TOT_ETC_AMT) AS TOT_ETC_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS TOT_PAY_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS PAY${item}
FROM TB_SL_DAILY_PROD_DLVR tsdpd, TB_MS_PRODUCT tmp, TB_MS_PRODUCT_BARCD tmpb WHERE tsdpd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdpd.STORE_CD = tmp.STORE_CD AND tsdpd.PROD_CD = tmp.PROD_CD AND tsdpd.STORE_CD = tmpb.STORE_CD(+) AND tsdpd.PROD_CD = tmpb.PROD_CD(+) AND tsdpd.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdpd.STORE_CD IN
#{item}
AND tsdpd.DLVR_PACK_FG = #{dlvrPackFg}
AND tsdpd.DLVR_IN_FG = #{dlvrInFg}
AND tmp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmp.PROD_CLASS_CD IN (SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS tmpc WHERE tmpc.STORE_CD = tsdpd.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND tmpc.STORE_CD = tsdpd.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND tmpc.STORE_CD = tsdpd.STORE_CD )
GROUP BY tsdpd.DLVR_PACK_FG, tsdpd.PROD_CD, tmp.PROD_NM, tmpb.BARCD_CD ORDER BY tsdpd.DLVR_PACK_FG, tsdpd.PROD_CD
SELECT tsdpd.DLVR_PACK_FG, tsdpd.PROD_CD, tmp.PROD_NM, NVL(tmpb.BARCD_CD, '') AS BAR_CD, SUM(tsdpd.TOT_SALE_QTY) AS SALE_QTY, SUM(tsdpd.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdpd.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdpd.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(tsdpd.REAL_SALE_AMT) - SUM(tsdpd.TOT_VAT_AMT) AS TOT_GA_AMT, SUM(tsdpd.TOT_VAT_AMT) AS TOT_VAT_AMT, SUM(tsdpd.TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(tsdpd.TOT_ETC_AMT) AS TOT_ETC_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS TOT_PAY_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS PAY${item}
FROM TB_SL_DAILY_PROD_DLVR tsdpd, TB_MS_PRODUCT tmp, TB_MS_PRODUCT_BARCD tmpb WHERE tsdpd.STORE_CD = #{storeCd} AND tsdpd.STORE_CD = tmp.STORE_CD AND tsdpd.PROD_CD = tmp.PROD_CD AND tsdpd.STORE_CD = tmpb.STORE_CD(+) AND tsdpd.PROD_CD = tmpb.PROD_CD(+) AND tsdpd.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdpd.DLVR_PACK_FG = #{dlvrPackFg}
AND tsdpd.DLVR_IN_FG = #{dlvrInFg}
AND tmp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmp.PROD_CLASS_CD IN (SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = #{storeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = #{storeCd} )
GROUP BY tsdpd.DLVR_PACK_FG, tsdpd.PROD_CD, tmp.PROD_NM, tmpb.BARCD_CD ORDER BY tsdpd.DLVR_PACK_FG, tsdpd.PROD_CD

/* DlvrFgMapper.getProdDtl */
SELECT tsdpd.DLVR_PACK_FG, tsdpd.DLVR_IN_FG, tsdpd.PROD_CD, tmp.PROD_NM, NVL(tmpb.BARCD_CD, '') AS BAR_CD, SUM(tsdpd.TOT_SALE_QTY) AS SALE_QTY, SUM(tsdpd.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdpd.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdpd.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(tsdpd.REAL_SALE_AMT) - SUM(tsdpd.TOT_VAT_AMT) AS TOT_GA_AMT, SUM(tsdpd.TOT_VAT_AMT) AS TOT_VAT_AMT, SUM(tsdpd.TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(tsdpd.TOT_ETC_AMT) AS TOT_ETC_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS TOT_PAY_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS PAY${item}
FROM TB_SL_DAILY_PROD_DLVR tsdpd, TB_MS_PRODUCT tmp, TB_MS_PRODUCT_BARCD tmpb WHERE tsdpd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdpd.STORE_CD = tmp.STORE_CD AND tsdpd.PROD_CD = tmp.PROD_CD AND tsdpd.STORE_CD = tmpb.STORE_CD(+) AND tsdpd.PROD_CD = tmpb.PROD_CD(+) AND tsdpd.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdpd.STORE_CD IN
#{item}
AND tsdpd.DLVR_PACK_FG = #{dlvrPackFg}
AND tsdpd.DLVR_IN_FG = #{dlvrInFg}
AND tmp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmp.PROD_CLASS_CD IN (SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS tmpc WHERE tmpc.STORE_CD = tsdpd.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND tmpc.STORE_CD = tsdpd.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND tmpc.STORE_CD = tsdpd.STORE_CD )
GROUP BY tsdpd.DLVR_PACK_FG, tsdpd.DLVR_IN_FG, tsdpd.PROD_CD, tmp.PROD_NM, tmpb.BARCD_CD ORDER BY tsdpd.DLVR_PACK_FG, tsdpd.DLVR_IN_FG, tsdpd.PROD_CD
SELECT tsdpd.DLVR_PACK_FG, tsdpd.DLVR_IN_FG, tsdpd.PROD_CD, tmp.PROD_NM, NVL(tmpb.BARCD_CD, '') AS BAR_CD, SUM(tsdpd.TOT_SALE_QTY) AS SALE_QTY, SUM(tsdpd.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdpd.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdpd.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(tsdpd.REAL_SALE_AMT) - SUM(tsdpd.TOT_VAT_AMT) AS TOT_GA_AMT, SUM(tsdpd.TOT_VAT_AMT) AS TOT_VAT_AMT, SUM(tsdpd.TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(tsdpd.TOT_ETC_AMT) AS TOT_ETC_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS TOT_PAY_AMT,
SUM(tsdpd.PAY_AMT_${item}) AS PAY${item}
FROM TB_SL_DAILY_PROD_DLVR tsdpd, TB_MS_PRODUCT tmp, TB_MS_PRODUCT_BARCD tmpb WHERE tsdpd.STORE_CD = #{storeCd} AND tsdpd.STORE_CD = tmp.STORE_CD AND tsdpd.PROD_CD = tmp.PROD_CD AND tsdpd.STORE_CD = tmpb.STORE_CD(+) AND tsdpd.PROD_CD = tmpb.PROD_CD(+) AND tsdpd.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdpd.DLVR_PACK_FG = #{dlvrPackFg}
AND tsdpd.DLVR_IN_FG = #{dlvrInFg}
AND tmp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmp.PROD_CLASS_CD IN (SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = #{storeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = #{storeCd} )
GROUP BY tsdpd.DLVR_PACK_FG, tsdpd.DLVR_IN_FG, tsdpd.PROD_CD, tmp.PROD_NM, tmpb.BARCD_CD ORDER BY tsdpd.DLVR_PACK_FG, tsdpd.DLVR_IN_FG, tsdpd.PROD_CD
