mapper.select:
/* DlvrRateMapper.getDlvrRateList */
SELECT SALE_DATE , COUNT(STORE_CD) AS STORE_CNT , SUM(CASE WHEN DLVR_ORDER_FG = '1' THEN BILL_CNT ELSE 0 END) AS BILL_CNT_STIN , SUM(CASE WHEN DLVR_ORDER_FG = '2' THEN BILL_CNT ELSE 0 END) AS BILL_CNT_DLVR , SUM(CASE WHEN DLVR_ORDER_FG = '3' THEN BILL_CNT ELSE 0 END) AS BILL_CNT_PICK , SUM(BILL_CNT) AS BILL_CNT , SUM(CASE WHEN DLVR_ORDER_FG = '1' THEN REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT_STIN , SUM(CASE WHEN DLVR_ORDER_FG = '2' THEN REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT_DLVR , SUM(CASE WHEN DLVR_ORDER_FG = '3' THEN REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT_PICK , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT , DECODE(SUM(REAL_SALE_AMT), 0, 0, NVL(ROUND(SUM(CASE WHEN DLVR_ORDER_FG = '1' THEN REAL_SALE_AMT ELSE 0 END) / SUM(REAL_SALE_AMT) * 100, 2), 0)) AS STIN_RATE , DECODE(SUM(REAL_SALE_AMT), 0, 0, NVL(ROUND(SUM(CASE WHEN DLVR_ORDER_FG = '2' THEN REAL_SALE_AMT ELSE 0 END) / SUM(REAL_SALE_AMT) * 100, 2), 0)) AS DLVR_RATE , DECODE(SUM(REAL_SALE_AMT), 0, 0, NVL(ROUND(SUM(CASE WHEN DLVR_ORDER_FG = '3' THEN REAL_SALE_AMT ELSE 0 END) / SUM(REAL_SALE_AMT) * 100, 2), 0)) AS PICK_RATE
FROM TB_SL_DAILY_TOTAL_DLVR tsdtd WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND STORE_CD IN
#{item}
GROUP BY SALE_DATE

