mapper.select:
/* USE : OrderChannelMapper.getGuestColList */
SELECT A.NMCODE_CD AS GUEST_CD, NVL(B.NMCODE_NM, '미등록') AS GUEST_NM FROM TB_CM_NMCODE A, TB_HQ_NMCODE B WHERE 1=1 AND A.NMCODE_GRP_CD = '119' AND B.HQ_OFFICE_CD (+)= #{hqOfficeCd} AND B.NMCODE_GRP_CD (+)= A.NMCODE_GRP_CD AND B.NMCODE_CD (+)= A.NMCODE_CD ORDER BY A.NMCODE_CD
SELECT A.NMCODE_CD AS GUEST_CD, NVL(B.NMCODE_NM, '미등록') AS GUEST_NM FROM TB_CM_NMCODE A, TB_MS_STORE_NMCODE B WHERE 1=1 AND A.NMCODE_GRP_CD = '119' AND B.STORE_CD (+)= #{storeCd} AND B.NMCODE_GRP_CD (+)= A.NMCODE_GRP_CD AND B.NMCODE_CD (+)= A.NMCODE_CD ORDER BY A.NMCODE_CD

/* USE : OrderChannelMapper.getOrderChannelPeriodList */ SELECT tsdto.DLVR_IN_FG, tcn.NMCODE_NM AS DLVR_IN_FG_NM, SUM(tsdto.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdto.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdto.REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(tsdto.REAL_SALE_AMT) - SUM(tsdto.VAT_AMT) AS GA_AMT, SUM(tsdto.VAT_AMT) AS VAT_AMT, SUM(tsdto.TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(tsdto.TOT_ETC_AMT) AS TOT_ETC_AMT, SUM(tsdto.BILL_CNT) AS BILL_CNT, DECODE(SUM(tsdto.BILL_CNT), 0, 0, ROUND(SUM(tsdto.REAL_SALE_AMT) / SUM(tsdto.BILL_CNT))) AS BILL_UPRC,
NVL(SUM(tsdto.PAY_AMT_${item}), 0) AS TOT_PAY_AMT,
NVL(SUM(tsdto.PAY_AMT_${item}), 0) AS PAY${item} ,
NVL(SUM(tsdto.DC_AMT_${item}), 0) AS DC${item} , SUM(tsdto.TOT_GUEST_CNT) AS TOT_GUEST_CNT, SUM(tsdto.GUEST_CNT_1) AS GUEST01, SUM(tsdto.GUEST_CNT_2) AS GUEST02, SUM(tsdto.GUEST_CNT_3) AS GUEST03, SUM(tsdto.GUEST_CNT_4) AS GUEST04, SUM(tsdto.GUEST_CNT_5) AS GUEST05, SUM(tsdto.GUEST_CNT_6) AS GUEST06, DECODE(NVL(SUM(tsdto.TOT_GUEST_CNT),0), 0, 0, ROUND(SUM(tsdto.REAL_SALE_AMT) / SUM(tsdto.TOT_GUEST_CNT))) AS GUEST_UPRC FROM TB_SL_DAILY_TOTAL_ORDAPP tsdto, (
SELECT '0' AS NMCODE_CD, '일반' AS NMCODE_NM FROM DUAL UNION SELECT NMCODE_CD, NMCODE_NM FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '112'
SELECT '0' AS NMCODE_CD, '일반' AS NMCODE_NM FROM DUAL UNION SELECT '1' AS NMCODE_CD, 'CID' AS NMCODE_NM FROM DUAL UNION SELECT NMCODE_CD, NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '113'
SELECT '0' AS NMCODE_CD, '일반' AS NMCODE_NM FROM DUAL UNION SELECT '1' AS NMCODE_CD, 'CID' AS NMCODE_NM FROM DUAL UNION SELECT NMCODE_CD, NMCODE_NM FROM TB_MS_STORE_NMCODE WHERE STORE_CD = #{storeCd} AND NMCODE_GRP_CD = '113'
) tcn WHERE tsdto.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdto.STORE_CD IN
#{item}
AND tsdto.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsdto.DLVR_IN_FG = tcn.NMCODE_CD GROUP BY tsdto.DLVR_IN_FG, tcn.NMCODE_NM ORDER BY TO_NUMBER(tsdto.DLVR_IN_FG)

/* USE : OrderChannelMapper.getOrderChannelDayList */
SELECT TO_CHAR(TO_DATE(tsdto.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE,
SUM(tsdto.BILL_CNT) AS TOT_BILL_CNT,
SUM(tsdto.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT,

    SUM(CASE WHEN tsdto.DLVR_IN_FG = #{item} THEN tsdto.BILL_CNT ELSE 0 END) AS BILL_CNT_${item},
    SUM(CASE WHEN tsdto.DLVR_IN_FG = #{item} THEN tsdto.REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT_${item}

FROM TB_SL_DAILY_TOTAL_ORDAPP tsdto
WHERE 1=1
AND tsdto.HQ_OFFICE_CD = #{hqOfficeCd}

    AND tsdto.STORE_CD IN

        #{item}


AND tsdto.SALE_DATE BETWEEN #{startDate} AND #{endDate}
GROUP
BY tsdto.SALE_DATE
ORDER
BY tsdto.SALE_DATE DESC

/* USE : OrderChannelMapper.getOrderChannelDtlList */ SELECT tssh.STORE_CD, tms.STORE_NM, tssh.POS_NO, tssh.BILL_NO, tssh.REAL_SALE_AMT, tsshd.CHANNEL_ORDER_NO, tsshd.VORDER_NO FROM TB_SL_SALE_HDR tssh, TB_SL_SALE_HDR_DLVR tsshd, TB_MS_STORE tms WHERE 1=1 AND tssh.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssh.STORE_CD IN
#{item}
AND tssh.SALE_DATE = #{saleDate} AND tsshd.DLVR_IN_FG = #{dlvrInFg} AND tsshd.HQ_OFFICE_CD = tssh.HQ_OFFICE_CD AND tsshd.STORE_CD = tssh.STORE_CD AND tsshd.SALE_DATE = tssh.SALE_DATE AND tsshd.POS_NO = tssh.POS_NO AND tsshd.BILL_NO = tssh.BILL_NO AND tms.HQ_OFFICE_CD = tssh.HQ_OFFICE_CD AND tms.STORE_CD = tssh.STORE_CD ORDER BY tssh.STORE_CD, tms.STORE_NM, tssh.POS_NO, tssh.BILL_NO

/* USE : OrderChannelMapper.getOrderChannelMonthList */
SELECT TO_CHAR(TO_DATE(tsdto.SALE_DATE, 'yyyymmdd'), 'yyyy-mm') AS SALE_DATE,
        SUM(tsdto.BILL_CNT) AS TOT_BILL_CNT,
        SUM(tsdto.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT,

            SUM(CASE WHEN tsdto.DLVR_IN_FG = #{item} THEN tsdto.BILL_CNT ELSE 0 END) AS BILL_CNT_${item},
            SUM(CASE WHEN tsdto.DLVR_IN_FG = #{item} THEN tsdto.REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT_${item}

 FROM TB_SL_DAILY_TOTAL_ORDAPP tsdto
WHERE 1=1
  AND tsdto.HQ_OFFICE_CD = #{hqOfficeCd}

    AND tsdto.STORE_CD IN

        #{item}


  AND tsdto.SALE_DATE BETWEEN #{startDate} AND #{endDate}
GROUP
   BY TO_CHAR(TO_DATE(tsdto.SALE_DATE, 'yyyymmdd'), 'yyyy-mm')
ORDER
   BY TO_CHAR(TO_DATE(tsdto.SALE_DATE, 'yyyymmdd'), 'yyyy-mm') ASC
