mapper.select:
/* DlvrStatusMapper.getDlvrStatusList */ SELECT A.STORE_CD , C.STORE_NM , NVL(SUM(A.REAL_SALE_CNT),0) AS DLVR_BILL_CNT , NVL(SUM(B.REAL_SALE_AMT),0) AS DLVR_AMT FROM TB_SL_DAILY_TOTAL_DLVR A , ( SELECT A.STORE_CD , A.SALE_DATE , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_PROD A WHERE 1=1 AND A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND A.DLVR_PACK_FG = '2' AND A.PROD_CD IN (SELECT PROD_CD FROM TB_HQ_PRODUCT WHERE HQ_OFFICE_CD= #{hqOfficeCd} AND PROD_CLASS_CD = #{prodClassCd}) GROUP BY A.STORE_CD , A.SALE_DATE ) B , TB_MS_STORE C WHERE B.STORE_CD (+) = A.STORE_CD AND B.SALE_DATE (+) = A.SALE_DATE AND C.STORE_CD = A.STORE_CD AND A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND A.DLVR_ORDER_FG = '2'
AND A.STORE_CD IN (${storeCdQuery})
GROUP BY A.STORE_CD , C.STORE_NM ORDER BY A.STORE_CD
