mapper.select:
/* DlvrStatusMapper.getDlvrStatusList */ SELECT A.STORE_CD , C.STORE_NM , NVL(SUM(B.REAL_SALE_CNT),0) AS DLVR_BILL_CNT , NVL(SUM(A.REAL_SALE_AMT),0) AS DLVR_AMT FROM ( SELECT A.HQ_OFFICE_CD , A.STORE_CD , A.SALE_DATE , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_PROD A , TB_MS_PRODUCT B WHERE 1=1 AND B.STORE_CD = A.STORE_CD AND B.PROD_CD = A.PROD_CD AND A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND B.PROD_CLASS_CD = #{prodClassCd} AND B.USE_YN = 'Y'
AND A.STORE_CD IN (${storeCdQuery})
GROUP BY A.HQ_OFFICE_CD , A.STORE_CD , A.SALE_DATE ) A , TB_SL_DAILY_TOTAL_DLVR B , TB_MS_STORE C WHERE 1=1 AND B.HQ_OFFICE_CD (+) = A.HQ_OFFICE_CD AND B.STORE_CD (+) = A.STORE_CD AND B.SALE_DATE (+) = A.SALE_DATE AND C.STORE_CD = A.STORE_CD AND B.DLVR_ORDER_FG = '2' GROUP BY A.STORE_CD , C.STORE_NM ORDER BY A.STORE_CD
