mapper.select:
/* BillSaleMomsMapper.getBillSaleMomsList */
SELECT TO_CHAR(TO_DATE(tssd2.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, tssd2.BILL_NO, tssd2.STORE_CD, tssd2.STORE_NM, tssd2.PROD_NM, SUM(CASE WHEN tssd2.SEL_TYPE_FG = 'N' OR tssd2.SEL_TYPE_FG = 'P' THEN tssd2.SALE_QTY END) AS SALE_QTY1, SUM(CASE WHEN tssd2.SEL_TYPE_FG = 'N' OR tssd2.SEL_TYPE_FG = 'P' THEN tssd2.TOT_SALE_AMT END) AS TOT_SALE_AMT1, SUM(CASE WHEN tssd2.SEL_TYPE_FG = 'N' OR tssd2.SEL_TYPE_FG = 'P' THEN tssd2.REAL_SALE_AMT END) AS REAL_SALE_AMT1
FROM ( SELECT tssd.SALE_DATE, tssd.BILL_NO, tssd.STORE_CD, tms.STORE_NM, tssd.PROD_CD, tmp.PROD_NM, SUM(tssd.SALE_QTY) AS SALE_QTY, SUM(tssd.SALE_AMT) AS TOT_SALE_AMT, SUM(tssd.REAL_SALE_AMT) AS REAL_SALE_AMT, (CASE WHEN ( tssd.SIDE_P_DTL_NO = tssd.BILL_DTL_NO OR tssd.SIDE_P_PROD_CD = tssd.PROD_CD OR ( tssd.SIDE_P_DTL_NO IS NOT NULL AND TRIM(tssd.SIDE_P_PROD_CD) IS NULL ) ) THEN ( CASE WHEN tssd.SINGLE_PROD_CD IS NOT NULL THEN 'N' ELSE 'P' END ) WHEN ( tssd.SIDE_P_DTL_NO != tssd.BILL_DTL_NO AND TRIM(tssd.SIDE_P_PROD_CD) IS NOT NULL ) THEN 'S' ELSE 'N' END) AS SEL_TYPE_FG FROM TB_SL_SALE_DTL tssd, TB_MS_STORE tms, TB_MS_STORE_INFO tmsi, TB_MS_PRODUCT tmp WHERE 1=1 AND tssd.HQ_OFFICE_CD = #{hqOfficeCd} AND tssd.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tssd.STORE_CD IN (${storeCdQuery})
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = tmp.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = tmp.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = tmp.STORE_CD )
AND tmp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND
tmp.PROD_CD = #{item}
AND tmp.HQ_BRAND_CD = #{prodHqBrandCd} -- 상품브랜드 선택일때
-- 상품브랜드 전체일때 AND tmp.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tmsi.MOMS_STORE_FG_02 = #{momsStoreFg02}
AND tmsi.MOMS_STORE_FG_03 = #{momsStoreFg03}
AND tmsi.MOMS_STORE_FG_04 = #{momsStoreFg04}
AND tmsi.MOMS_STORE_FG_05 = #{momsStoreFg05}
AND tms.STORE_CD = tssd.STORE_CD AND tmsi.STORE_CD (+)= tms.STORE_CD AND tmp.STORE_CD = tssd.STORE_CD AND tmp.PROD_CD = tssd.PROD_CD GROUP BY tssd.SALE_DATE, tssd.BILL_NO, tssd.STORE_CD, tms.STORE_NM, tssd.PROD_CD, tmp.PROD_NM, tssd.SIDE_P_DTL_NO, tssd.BILL_DTL_NO, tssd.SIDE_P_PROD_CD, tssd.SINGLE_PROD_CD ) tssd2 WHERE 1=1 AND (tssd2.SEL_TYPE_FG = 'N' OR tssd2.SEL_TYPE_FG = 'P') -- 단품/세트 GROUP BY tssd2.SALE_DATE, tssd2.BILL_NO, tssd2.STORE_CD, tssd2.STORE_NM, tssd2.PROD_CD, tssd2.PROD_NM ORDER BY tssd2.SALE_DATE, tssd2.BILL_NO, tssd2.STORE_CD, tssd2.PROD_CD

